import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Linkedin, Video, FileText, Database, TrendingUp, Users, Lightbulb, Copy, Settings, Plus, Trash2, Edit3, Save, X, Search, BookOpen, UserPlus, ChevronLeft, ChevronRight, Upload, Globe, AlertCircle, CheckCircle, Clock } from 'lucide-react';

// ðŸ”§ CONFIGURATION - Your Working Backend URL
const BACKEND_CONFIG = {
  url: 'https://linkedin-content-creator-api-11874687899.asia-south1.run.app',
};

// Cloud Document Manager - Uses your working backend
const createCloudDocumentManager = () => {
  const BACKEND_URL = BACKEND_CONFIG.url;

  return {
    // Upload document as research item
    uploadDocument: async (file) => {
      try {
        console.log('Uploading file:', file.name);

        const researchItem = {
          topic: `Document: ${file.name}`,
          findings: `Uploaded document: ${file.name} (${file.size} bytes)`,
          data: `Document content from ${file.name} (${file.size} bytes)`,
          source: file.name,
          tags: `document, ${file.type}, upload`
        };

        const response = await fetch(`${BACKEND_URL}/linkedin/research`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(researchItem)
        });

        if (!response.ok) {
          throw new Error(`Upload failed: ${response.status}`);
        }

        const result = await response.json();
        return {
          filename: file.name,
          chunks_added: 1,
          total_chunks: 1,
          document_id: result.id,
          gcs_url: `research://${result.id}`
        };
      } catch (error) {
        console.error('Upload error:', error);
        throw error;
      }
    },

    // Get documents from research items
    getDocuments: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/linkedin/research`);

        if (!response.ok) {
          throw new Error(`Get documents failed: ${response.status}`);
        }

        const researchItems = await response.json();
        const documents = researchItems.filter(item => 
          item.tags && item.tags.includes('document')
        );

        return documents.map(doc => ({
          filename: doc.source || doc.topic,
          total_chunks: 1,
          upload_date: doc.created_at,
          file_size: doc.data ? doc.data.length : 0,
          file_type: 'research',
          document_id: doc.id,
          gcs_url: `research://${doc.id}`
        }));
      } catch (error) {
        console.error('Get documents error:', error);
        throw error;
      }
    },

    // Delete document
    deleteDocument: async (filename) => {
      try {
        const response = await fetch(`${BACKEND_URL}/linkedin/research`);
        const researchItems = await response.json();

        const documentItem = researchItems.find(item => 
          (item.source === filename || item.topic === `Document: ${filename}`) && 
          item.tags && item.tags.includes('document')
        );

        if (!documentItem) {
          throw new Error(`Document ${filename} not found`);
        }

        const deleteResponse = await fetch(`${BACKEND_URL}/linkedin/research/${documentItem.id}`, {
          method: 'DELETE'
        });

        if (!deleteResponse.ok) {
          throw new Error(`Delete failed: ${deleteResponse.status}`);
        }

        return await deleteResponse.json();
      } catch (error) {
        console.error('Delete error:', error);
        throw error;
      }
    },

    // Ask questions using research items
    askQuestion: async (question) => {
      try {
        const researchResponse = await fetch(`${BACKEND_URL}/linkedin/research`);
        const researchItems = await researchResponse.json();

        const relevantItems = researchItems.filter(item => 
          item.topic.toLowerCase().includes(question.toLowerCase()) ||
          item.findings.toLowerCase().includes(question.toLowerCase()) ||
          item.data?.toLowerCase().includes(question.toLowerCase())
        );

        if (relevantItems.length === 0) {
          return {
            answer: "I don't have enough information to answer that question. Please upload some relevant documents or research first.",
            sources: [],
            confidence: 0.0
          };
        }

        const context = relevantItems[0];
        const answer = `Based on the research "${context.topic}": ${context.findings}. ${context.data || ''}`;

        return {
          answer: answer,
          sources: relevantItems.map(item => ({
            title: item.topic,
            content: item.findings,
            source: item.source
          })),
          confidence: 0.8
        };
      } catch (error) {
        console.error('Ask error:', error);
        throw error;
      }
    },

    // Generate content using the working backend
    generateContent: async (prompt, creatorStyle, researchContext) => {
      try {
        let creatorKey = 'gary-v';
        if (creatorStyle.toLowerCase().includes('simon')) {
          creatorKey = 'simon-sinek';
        } else if (creatorStyle.toLowerCase().includes('seth')) {
          creatorKey = 'seth-godin';
        }

        const researchResponse = await fetch(`${BACKEND_URL}/linkedin/research`);
        const researchItems = await researchResponse.json();
        const researchIds = researchItems.map(item => item.id);

        const response = await fetch(`${BACKEND_URL}/linkedin/generate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            prompt,
            creator_key: creatorKey,
            research_ids: researchIds
          })
        });

        if (!response.ok) {
          throw new Error(`Generate failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Generate error:', error);
        throw error;
      }
    },

    // Health check
    healthCheck: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/`);
        return response.ok;
      } catch (error) {
        return false;
      }
    }
  };
};

// Main LinkedIn Content Bot Component
const LinkedInContentBot = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedCreator, setSelectedCreator] = useState('gary-v');
  const [documents, setDocuments] = useState([]);
  const [research, setResearch] = useState([]);
  const [creators, setCreators] = useState([]);
  const [activeTab, setActiveTab] = useState('chat');
  const [backendStatus, setBackendStatus] = useState('checking');
  const [uploadedFile, setUploadedFile] = useState(null);
  const [showConfig, setShowConfig] = useState(false);
  const [generatedContent, setGeneratedContent] = useState(null);

  const messagesEndRef = useRef(null);
  const cloudManager = createCloudDocumentManager();

  // Test backend connection
  const testBackendConnection = async () => {
    try {
      const isHealthy = await cloudManager.healthCheck();
      setBackendStatus(isHealthy ? 'connected' : 'disconnected');
    } catch (error) {
      setBackendStatus('disconnected');
    }
  };

  // Backend status component
  const BackendStatus = () => (
    <div className="flex items-center gap-2 text-sm">
      <div className={`w-2 h-2 rounded-full ${
        backendStatus === 'connected' ? 'bg-green-500' : 
        backendStatus === 'checking' ? 'bg-yellow-500' : 'bg-red-500'
      }`}></div>
      <span className={backendStatus === 'connected' ? 'text-green-600' : 'text-red-600'}>
        {backendStatus === 'connected' ? 'Backend: Connected' : 
         backendStatus === 'checking' ? 'Backend: Checking...' : 'Backend: Disconnected'}
      </span>
    </div>
  );

  // Generate content with FastAPI backend
  const generateContentWithFastAPI = async (prompt, creatorStyle, relevantResearch) => {
    try {
      setIsLoading(true);

      const result = await cloudManager.generateContent(prompt, creatorStyle, relevantResearch);

      setGeneratedContent(result);

      const responseMessage = {
        id: Date.now(),
        type: 'assistant',
        content: `Generated content successfully! Check the Content tab to see your LinkedIn posts, video scripts, hashtags, and engagement tips.`,
        timestamp: new Date().toISOString()
      };

      setMessages(prev => [...prev, responseMessage]);

    } catch (error) {
      console.error('Content generation error:', error);
      const errorMessage = {
        id: Date.now(),
        type: 'assistant',
        content: `Error generating content: ${error.message}`,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Scroll to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Load research and creators on component mount
  useEffect(() => {
    const loadData = async () => {
      try {
        // Load research
        const researchResponse = await fetch(`${BACKEND_CONFIG.url}/linkedin/research`);
        if (researchResponse.ok) {
          const researchData = await researchResponse.json();
          setResearch(researchData);
        }

        // Load creators
        const creatorsResponse = await fetch(`${BACKEND_CONFIG.url}/linkedin/creators`);
        if (creatorsResponse.ok) {
          const creatorsData = await creatorsResponse.json();
          setCreators(creatorsData);
        }

        // Load documents
        const documentsData = await cloudManager.getDocuments();
        setDocuments(documentsData);

      } catch (error) {
        console.error('Error loading data:', error);
      }
    };

    loadData();
    testBackendConnection();
  }, []);

  // Handle send message
  const handleSendMessage = async (messageText) => {
    if (!messageText.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: messageText,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      // Check if it's a content generation request
      if (messageText.toLowerCase().includes('generate') || messageText.toLowerCase().includes('create')) {
        await generateContentWithFastAPI(messageText, selectedCreator, research);
      } else {
        // Handle as a question
        const result = await cloudManager.askQuestion(messageText);

        const responseMessage = {
          id: Date.now() + 1,
          type: 'assistant',
          content: result.answer,
          timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, responseMessage]);
      }
    } catch (error) {
      console.error('Error processing message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: `Error: ${error.message}`,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle creator selection
  const handleCreatorSelect = (creatorKey) => {
    setSelectedCreator(creatorKey);
  };

  // Copy to clipboard
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  // Handle file selection
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
    }
  };

  // Handle upload document
  const handleUploadDocument = async () => {
    if (!uploadedFile) return;

    try {
      setIsLoading(true);
      await cloudManager.uploadDocument(uploadedFile);

      // Refresh documents
      const documentsData = await cloudManager.getDocuments();
      setDocuments(documentsData);

      setUploadedFile(null);
      alert('Document uploaded successfully!');
    } catch (error) {
      console.error('Upload error:', error);
      alert(`Upload failed: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle delete document
  const handleDeleteDocument = async (filename) => {
    if (!confirm(`Are you sure you want to delete ${filename}?`)) return;

    try {
      await cloudManager.deleteDocument(filename);

      // Refresh documents
      const documentsData = await cloudManager.getDocuments();
      setDocuments(documentsData);

      alert('Document deleted successfully!');
    } catch (error) {
      console.error('Delete error:', error);
      alert(`Delete failed: ${error.message}`);
    }
  };

  // Configuration panel
  const ConfigPanel = () => (
    <div className="bg-white p-4 rounded-lg shadow-md">
      <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
        <Settings className="w-5 h-5" />
        Configuration
      </h3>

      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Backend URL
          </label>
          <input
            type="text"
            value={BACKEND_CONFIG.url}
            readOnly
            className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50"
            placeholder="Backend URL"
          />
        </div>

        <BackendStatus />
      </div>
    </div>
  );

  // Content display component
  const ContentDisplay = ({ content }) => {
    if (!content) return null;

    return (
      <div className="space-y-6">
        {/* LinkedIn Posts */}
        {content.linkedin_posts && content.linkedin_posts.length > 0 && (
          <div className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
              <Linkedin className="w-5 h-5 text-blue-600" />
              LinkedIn Posts ({content.linkedin_posts.length})
            </h3>
            <div className="space-y-3">
              {content.linkedin_posts.map((post, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-3 bg-gray-50">
                  <div className="flex justify-between items-start mb-2">
                    <span className="text-sm font-medium text-gray-600">Post {index + 1}</span>
                    <button
                      onClick={() => copyToClipboard(post)}
                      className="text-blue-600 hover:text-blue-800 text-sm flex items-center gap-1"
                    >
                      <Copy className="w-4 h-4" />
                      Copy
                    </button>
                  </div>
                  <p className="text-gray-800 whitespace-pre-wrap">{post}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Video Scripts */}
        {content.video_scripts && content.video_scripts.length > 0 && (
          <div className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
              <Video className="w-5 h-5 text-red-600" />
              Video Scripts ({content.video_scripts.length})
            </h3>
            <div className="space-y-3">
              {content.video_scripts.map((script, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-3 bg-gray-50">
                  <div className="flex justify-between items-start mb-2">
                    <span className="text-sm font-medium text-gray-600">Script {index + 1}</span>
                    <button
                      onClick={() => copyToClipboard(script)}
                      className="text-blue-600 hover:text-blue-800 text-sm flex items-center gap-1"
                    >
                      <Copy className="w-4 h-4" />
                      Copy
                    </button>
                  </div>
                  <p className="text-gray-800 whitespace-pre-wrap">{script}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Hashtags */}
        {content.hashtags && content.hashtags.length > 0 && (
          <div className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
              <TrendingUp className="w-5 h-5 text-purple-600" />
              Hashtags ({content.hashtags.length})
            </h3>
            <div className="flex flex-wrap gap-2">
              {content.hashtags.map((hashtag, index) => (
                <span key={index} className="bg-purple-100 text-purple-800 px-3 py-1 rounded-full text-sm">
                  {hashtag}
                </span>
              ))}
            </div>
            <button
              onClick={() => copyToClipboard(content.hashtags.join(' '))}
              className="mt-3 text-blue-600 hover:text-blue-800 text-sm flex items-center gap-1"
            >
              <Copy className="w-4 h-4" />
              Copy All Hashtags
            </button>
          </div>
        )}

        {/* Engagement Tips */}
        {content.engagement_tips && content.engagement_tips.length > 0 && (
          <div className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
              <Lightbulb className="w-5 h-5 text-yellow-600" />
              Engagement Tips ({content.engagement_tips.length})
            </h3>
            <ul className="space-y-2">
              {content.engagement_tips.map((tip, index) => (
                <li key={index} className="flex items-start gap-2">
                  <span className="text-yellow-600 mt-1">â€¢</span>
                  <span className="text-gray-800">{tip}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    );
  };

  // Research tab
  const ResearchTab = () => (
    <div className="space-y-4">
      <div className="bg-white p-4 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <BookOpen className="w-5 h-5" />
          Research Items ({research.length})
        </h3>

        <div className="space-y-3">
          {research.map((item) => (
            <div key={item.id} className="border border-gray-200 rounded-lg p-3">
              <h4 className="font-medium text-gray-900">{item.topic}</h4>
              <p className="text-gray-600 text-sm mt-1">{item.findings}</p>
              <div className="flex items-center gap-2 mt-2 text-xs text-gray-500">
                <span>Source: {item.source}</span>
                <span>â€¢</span>
                <span>{new Date(item.created_at).toLocaleDateString()}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Creator styles tab
  const CreatorTab = () => (
    <div className="space-y-4">
      <div className="bg-white p-4 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <Users className="w-5 h-5" />
          Creator Styles ({creators.length})
        </h3>

        <div className="space-y-3">
          {creators.map((creator) => (
            <div key={creator.id} className="border border-gray-200 rounded-lg p-3">
              <h4 className="font-medium text-gray-900">{creator.name}</h4>
              <p className="text-gray-600 text-sm mt-1">Key: {creator.key}</p>
              <p className="text-gray-600 text-sm mt-1">Tone: {creator.tone}</p>
              <p className="text-gray-600 text-sm mt-1">Structure: {creator.structure}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Documents tab
  const DocumentsTab = () => (
    <div className="space-y-4">
      <div className="bg-white p-4 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <FileText className="w-5 h-5" />
          Uploaded Documents ({documents.length})
        </h3>

        {/* Upload section */}
        <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
          <h4 className="font-medium mb-3">Upload New Document</h4>
          <div className="flex items-center gap-3">
            <input
              type="file"
              onChange={handleFileSelect}
              className="flex-1 px-3 py-2 border border-gray-300 rounded-md"
            />
            <button
              onClick={handleUploadDocument}
              disabled={!uploadedFile || isLoading}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2"
            >
              <Upload className="w-4 h-4" />
              Upload
            </button>
          </div>
          {uploadedFile && (
            <p className="text-sm text-gray-600 mt-2">Selected: {uploadedFile.name}</p>
          )}
        </div>

        {/* Documents list */}
        <div className="space-y-3">
          {documents.map((doc) => (
            <div key={doc.document_id} className="border border-gray-200 rounded-lg p-3 flex justify-between items-center">
              <div>
                <h4 className="font-medium text-gray-900">{doc.filename}</h4>
                <p className="text-gray-600 text-sm">Size: {doc.file_size} bytes</p>
                <p className="text-gray-600 text-sm">Uploaded: {new Date(doc.upload_date).toLocaleDateString()}</p>
              </div>
              <button
                onClick={() => handleDeleteDocument(doc.filename)}
                className="text-red-600 hover:text-red-800 p-2"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Chat tab
  const ChatTab = () => (
    <div className="flex flex-col h-full">
      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                message.type === 'user'
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-800'
              }`}
            >
              <p className="whitespace-pre-wrap">{message.content}</p>
              <p className="text-xs opacity-70 mt-1">
                {new Date(message.timestamp).toLocaleTimeString()}
              </p>
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-gray-200 text-gray-800 px-4 py-2 rounded-lg">
              <div className="flex items-center gap-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-600"></div>
                <span>Generating response...</span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex gap-2">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(inputMessage);
              }
            }}
            placeholder="Type your message..."
            className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isLoading}
          />
          <button
            onClick={() => handleSendMessage(inputMessage)}
            disabled={!inputMessage.trim() || isLoading}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2"
          >
            <Send className="w-4 h-4" />
            Send
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Bot className="w-8 h-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">LinkedIn Content Creator</h1>
                <p className="text-gray-600">AI-powered content generation with research integration</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <BackendStatus />
              <button
                onClick={() => setShowConfig(!showConfig)}
                className="p-2 text-gray-600 hover:text-gray-800"
              >
                <Settings className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>

        {/* Configuration Panel */}
        {showConfig && (
          <div className="mb-6">
            <ConfigPanel />
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-md p-4">
              <h3 className="text-lg font-semibold mb-4">Navigation</h3>

              <nav className="space-y-2">
                <button
                  onClick={() => setActiveTab('chat')}
                  className={`w-full flex items-center gap-2 px-3 py-2 rounded-md text-left ${
                    activeTab === 'chat' ? 'bg-blue-100 text-blue-700' : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <Bot className="w-4 h-4" />
                  Chat
                </button>

                <button
                  onClick={() => setActiveTab('content')}
                  className={`w-full flex items-center gap-2 px-3 py-2 rounded-md text-left ${
                    activeTab === 'content' ? 'bg-blue-100 text-blue-700' : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <Linkedin className="w-4 h-4" />
                  Generated Content
                </button>

                <button
                  onClick={() => setActiveTab('research')}
                  className={`w-full flex items-center gap-2 px-3 py-2 rounded-md text-left ${
                    activeTab === 'research' ? 'bg-blue-100 text-blue-700' : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <BookOpen className="w-4 h-4" />
                  Research ({research.length})
                </button>

                <button
                  onClick={() => setActiveTab('creators')}
                  className={`w-full flex items-center gap-2 px-3 py-2 rounded-md text-left ${
                    activeTab === 'creators' ? 'bg-blue-100 text-blue-700' : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <Users className="w-4 h-4" />
                  Creator Styles ({creators.length})
                </button>

                <button
                  onClick={() => setActiveTab('documents')}
                  className={`w-full flex items-center gap-2 px-3 py-2 rounded-md text-left ${
                    activeTab === 'documents' ? 'bg-blue-100 text-blue-700' : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <FileText className="w-4 h-4" />
                  Documents ({documents.length})
                </button>
              </nav>

              {/* Creator Selection */}
              <div className="mt-6 pt-6 border-t border-gray-200">
                <h4 className="font-medium mb-3">Creator Style</h4>
                <div className="space-y-2">
                  {creators.map((creator) => (
                    <button
                      key={creator.id}
                      onClick={() => handleCreatorSelect(creator.key)}
                      className={`w-full text-left px-3 py-2 rounded-md text-sm ${
                        selectedCreator === creator.key
                          ? 'bg-blue-100 text-blue-700'
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      {creator.name}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow-md h-96">
              {activeTab === 'chat' && <ChatTab />}
              {activeTab === 'content' && (
                <div className="p-4 overflow-y-auto h-full">
                  <ContentDisplay content={generatedContent} />
                </div>
              )}
              {activeTab === 'research' && (
                <div className="p-4 overflow-y-auto h-full">
                  <ResearchTab />
                </div>
              )}
              {activeTab === 'creators' && (
                <div className="p-4 overflow-y-auto h-full">
                  <CreatorTab />
                </div>
              )}
              {activeTab === 'documents' && (
                <div className="p-4 overflow-y-auto h-full">
                  <DocumentsTab />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LinkedInContentBot;
