import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Linkedin, Video, FileText, Database, TrendingUp, Users, Copy, Settings, Plus, Trash2, Edit3, Save, X, Search, BookOpen, UserPlus, ChevronLeft, ChevronRight, Upload, Globe, AlertCircle, CheckCircle, Clock } from 'lucide-react';

const BACKEND_CONFIG = {
  url: 'http://localhost:8000'
};

const createCloudDocumentManager = () => {
  const BACKEND_URL = BACKEND_CONFIG.url;

  return {
    healthCheck: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/`);
        return await response.json();
      } catch (error) {
        return { status: 'offline' };
      }
    },
    getResearch: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/research`);
        return response.ok ? await response.json() : [];
      } catch (error) {
        return [];
      }
    },
    createResearch: async (data) => {
      const response = await fetch(`${BACKEND_URL}/research`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      return await response.json();
    },
    deleteResearch: async (id) => {
      const response = await fetch(`${BACKEND_URL}/research/${id}`, { method: 'DELETE' });
      return await response.json();
    },
    updateResearch: async (id, data) => {
      const response = await fetch(`${BACKEND_URL}/research/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      return await response.json();
    },
    getProfiles: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/profiles`);
        return response.ok ? await response.json() : [];
      } catch (error) {
        return [];
      }
    },
    createProfile: async (data) => {
      const response = await fetch(`${BACKEND_URL}/profiles`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      return await response.json();
    },
    generateContent: async (prompt, creatorKey, researchIds) => {
      const response = await fetch(`${BACKEND_URL}/content/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, creator_key: creatorKey, research_ids: researchIds })
      });
      return await response.json();
    },
    uploadDocument: async (file) => {
      const formData = new FormData();
      formData.append('file', file);
      const response = await fetch(`${BACKEND_URL}/upload`, {
        method: 'POST',
        body: formData
      });
      return await response.json();
    },
    getDocuments: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/documents`);
        return response.ok ? await response.json() : [];
      } catch (error) {
        return [];
      }
    },
    deleteDocument: async (filename) => {
      const response = await fetch(`${BACKEND_URL}/documents/${encodeURIComponent(filename)}`, {
        method: 'DELETE'
      });
      return await response.json();
    },
    crawlWebsite: async (url) => {
      const response = await fetch(`${BACKEND_URL}/crawl`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url })
      });
      return await response.json();
    }
  };
};

let configStore = {
  openaiApiKey: '',
  model: 'gpt-4'
};

const LinkedInContentBot = () => {
  const [activeTab, setActiveTab] = useState('chat');
  const [messages, setMessages] = useState([{
    id: 1,
    type: 'bot',
    text: "Welcome to LinkedIn Content Creator! How can I help you today?",
    timestamp: new Date()
  }]);
  const [isTyping, setIsTyping] = useState(false);
  const [showConfig, setShowConfig] = useState(false);
  const [config, setConfig] = useState(configStore);
  const [generatedContent, setGeneratedContent] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState({ linkedinPost: 0, videoScript: 0 });
  const messagesEndRef = useRef(null);

  const [backendStatus, setBackendStatus] = useState('checking');
  const [backendError, setBackendError] = useState(null);
  const [documentManager, setDocumentManager] = useState(null);

  const [researchDatabase, setResearchDatabase] = useState([]);
  const [creatorDatabase, setCreatorDatabase] = useState([
    {
      id: 1,
      name: "Professional Thought Leader",
      key: "thought_leader",
      icon: TrendingUp,
      style: {
        tone: 'Authoritative, insightful',
        structure: 'Hook + insight + call to action',
        language: 'Professional terminology',
        length: 'Medium posts',
        hooks: ['What if I told you...', 'The future of business...'],
        endings: ['What are your thoughts?', 'How do you see this evolving?'],
        characteristics: 'Data-driven, leadership-oriented'
      }
    }
  ]);
  const [customDocuments, setCustomDocuments] = useState([]);
  const [promptHistory, setPromptHistory] = useState([]);

  const [isLoadingResearch, setIsLoadingResearch] = useState(false);
  const [isLoadingDocuments, setIsLoadingDocuments] = useState(false);
  const [editingResearch, setEditingResearch] = useState(null);
  const [editingCreator, setEditingCreator] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploadingDocument, setIsUploadingDocument] = useState(false);

  const initializeBackend = async () => {
    setBackendStatus('checking');
    const manager = createCloudDocumentManager();
    setDocumentManager(manager);

    try {
      const health = await manager.healthCheck();
      if (health.status && health.status.includes('online')) {
        setBackendStatus('connected');
        loadResearchFromBackend(manager);
        loadProfilesFromBackend(manager);
        loadDocumentsFromBackend(manager);
      } else {
        setBackendStatus('error');
        setBackendError('Backend offline');
      }
    } catch (error) {
      setBackendStatus('error');
      setBackendError(error.message);
    }
  };

  const loadResearchFromBackend = async (manager = documentManager) => {
    if (!manager) return;
    setIsLoadingResearch(true);
    try {
      const research = await manager.getResearch();
      setResearchDatabase(research.map(item => ({
        ...item,
        tags: typeof item.tags === 'string' ? item.tags.split(',').map(t => t.trim()) : (item.tags || []),
        dateAdded: new Date(item.created_at || Date.now())
      })));
    } catch (error) {
      console.error('Error loading research:', error);
    } finally {
      setIsLoadingResearch(false);
    }
  };

  const loadProfilesFromBackend = async (manager = documentManager) => {
    if (!manager) return;
    try {
      const profiles = await manager.getProfiles();
      if (profiles && profiles.length > 0) {
        setCreatorDatabase(profiles.map(profile => ({
          id: profile.id,
          name: profile.name,
          key: profile.key,
          icon: UserPlus,
          style: {
            tone: profile.tone,
            structure: profile.structure,
            language: profile.language,
            length: profile.length,
            hooks: profile.hooks ? profile.hooks.split(',').map(h => h.trim()).filter(h => h) : [],
            endings: profile.endings ? profile.endings.split(',').map(e => e.trim()).filter(e => e) : [],
            characteristics: profile.characteristics
          }
        })));
      }
    } catch (error) {
      console.error('Error loading profiles:', error);
    }
  };

  const loadDocumentsFromBackend = async (manager = documentManager) => {
    if (!manager) return;
    setIsLoadingDocuments(true);
    try {
      const documents = await manager.getDocuments();
      setCustomDocuments(documents || []);
    } catch (error) {
      console.error('Error loading documents:', error);
    } finally {
      setIsLoadingDocuments(false);
    }
  };

  useEffect(() => {
    initializeBackend();
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = async (messageText) => {
    if (!messageText?.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      text: messageText.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsTyping(true);

    try {
      let creatorStyle = creatorDatabase[0];
      const lowerText = messageText.toLowerCase();

      for (const creator of creatorDatabase) {
        if (lowerText.includes(creator.name.toLowerCase()) || lowerText.includes(creator.key)) {
          creatorStyle = creator;
          break;
        }
      }

      const mockContent = {
        linkedinPosts: [
          `🚀 ${messageText}\n\nThis is a sample LinkedIn post in ${creatorStyle.name} style.\n\n${creatorStyle.style.hooks[0] || 'Here\'s my take:'}\n\nWhat do you think? Share your thoughts below! 👇`,
          `💡 ${messageText}\n\nAlternative version in ${creatorStyle.name} style.\n\nKey insight: Success comes from consistent action.\n\n${creatorStyle.style.endings[0] || 'What\'s your experience?'}`
        ],
        videoScripts: [
          `[HOOK]\n${creatorStyle.style.hooks[0] || 'What if I told you...'}\n\n[SETUP]\n${messageText}\n\n[PAYOFF]\nHere's what I learned...\n\n[CTA]\nLike if you agree!`,
          `[INTRO]\nHey LinkedIn!\n\n[BODY]\n${messageText}\n\nThis reminds me of...\n\n[OUTRO]\n${creatorStyle.style.endings[0] || 'What do you think?'}`
        ],
        hashtags: ['#LinkedIn', '#Professional', '#Growth', '#Success'],
        engagement_tips: ['Post during peak hours', 'Engage with comments quickly', 'Use relevant hashtags']
      };

      setGeneratedContent(mockContent);

      const botResponse = {
        id: Date.now() + 1,
        type: 'bot',
        text: `Created content in ${creatorStyle.name} style! Use the arrows to switch between variations.`,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, botResponse]);
    } catch (error) {
      const errorResponse = {
        id: Date.now() + 1,
        type: 'bot',
        text: `Error: ${error.message}`,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleCreatorSelect = (creatorKey) => {
    const creator = creatorDatabase.find(c => c.key === creatorKey);
    if (creator) {
      handleSendMessage(`Create a ${creator.name} style LinkedIn post`);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  const handleAddResearch = async (researchData) => {
    if (!researchData.topic.trim()) return;

    try {
      if (backendStatus === 'connected' && documentManager) {
        await documentManager.createResearch(researchData);
        await loadResearchFromBackend();
      } else {
        const research = {
          id: Date.now(),
          topic: researchData.topic,
          findings: researchData.findings,
          source: researchData.source,
          data: researchData.data,
          tags: researchData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
          dateAdded: new Date()
        };
        setResearchDatabase(prev => [research, ...prev]);
      }
    } catch (error) {
      console.error('Error adding research:', error);
    }
  };

  const handleDeleteResearch = async (id) => {
    try {
      if (backendStatus === 'connected' && documentManager) {
        await documentManager.deleteResearch(id);
        await loadResearchFromBackend();
      } else {
        setResearchDatabase(prev => prev.filter(item => item.id !== id));
      }
    } catch (error) {
      console.error('Error deleting research:', error);
    }
  };

  const handleUpdateResearch = async (id, updatedData) => {
    try {
      if (backendStatus === 'connected' && documentManager) {
        await documentManager.updateResearch(id, updatedData);
        await loadResearchFromBackend();
      } else {
        setResearchDatabase(prev => prev.map(item => 
          item.id === id ? { ...item, ...updatedData, tags: updatedData.tags.split(',').map(t => t.trim()) } : item
        ));
      }
      setEditingResearch(null);
    } catch (error) {
      console.error('Error updating research:', error);
    }
  };

  const handleFileSelect = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUploadDocument = async () => {
    if (!selectedFile || !documentManager) return;

    setIsUploadingDocument(true);
    try {
      await documentManager.uploadDocument(selectedFile);
      await loadDocumentsFromBackend();
      setSelectedFile(null);
      const fileInput = document.getElementById('file-input');
      if (fileInput) fileInput.value = '';

      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'bot',
        text: `✅ Document uploaded successfully!`,
        timestamp: new Date()
      }]);
    } catch (error) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'bot',
        text: `❌ Upload failed: ${error.message}`,
        timestamp: new Date()
      }]);
    } finally {
      setIsUploadingDocument(false);
    }
  };

  const handleDeleteDocument = async (filename) => {
    if (!documentManager) return;
    try {
      await documentManager.deleteDocument(filename);
      await loadDocumentsFromBackend();
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'bot',
        text: `✅ Document deleted successfully!`,
        timestamp: new Date()
      }]);
    } catch (error) {
      console.error('Delete failed:', error);
    }
  };

  const BackendStatus = () => (
    <div className={`p-3 rounded-lg mb-4 flex items-center gap-2 ${
      backendStatus === 'connected' ? 'bg-green-50 border border-green-200' : 
      backendStatus === 'error' ? 'bg-red-50 border border-red-200' : 
      'bg-yellow-50 border border-yellow-200'
    }`}>
      {backendStatus === 'connected' && <CheckCircle className="w-4 h-4 text-green-600" />}
      {backendStatus === 'error' && <AlertCircle className="w-4 h-4 text-red-600" />}
      {backendStatus === 'checking' && <Clock className="w-4 h-4 text-yellow-600" />}

      <div className="flex-1">
        <p className={`text-sm font-medium ${
          backendStatus === 'connected' ? 'text-green-800' : 
          backendStatus === 'error' ? 'text-red-800' : 
          'text-yellow-800'
        }`}>
          Backend: {backendStatus === 'connected' ? 'Connected' : backendStatus === 'error' ? 'Disconnected' : 'Checking...'}
        </p>
        <p className="text-xs text-gray-600">URL: {BACKEND_CONFIG.url}</p>
        {backendError && <p className="text-xs text-red-600">{backendError}</p>}
      </div>
    </div>
  );

  const ConfigPanel = () => (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white p-8 rounded-xl w-11/12 max-w-md shadow-2xl">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">Configuration</h3>

        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">OpenAI API Key</label>
          <input
            type="password"
            value={config.openaiApiKey}
            onChange={(e) => setConfig(prev => ({ ...prev, openaiApiKey: e.target.value }))}
