sourcesContent:
import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Linkedin, Video, FileText, Database, TrendingUp, Users, Lightbulb, Copy, Settings, Plus, Trash2, Edit3, Save, X, Search, BookOpen, UserPlus, ChevronLeft, ChevronRight, Upload, Globe, AlertCircle, CheckCircle, Clock } from 'lucide-react';

// ðŸ”§ CONFIGURATION - UPDATE THIS WITH YOUR BACKEND URL
const BACKEND_CONFIG = {
  // ðŸš¨ CHANGE THIS TO YOUR DEPLOYED BACKEND URL
  url: 'http://localhost:8000', // Replace with your Cloud Run URL

  // For local development, use: 'http://localhost:8000'
  // For production, use: 'https://linkedin-content-creator-api-YOUR_HASH-uc.a.run.app'
};

// Enhanced Cloud Document Manager with all backend endpoints
const createCloudDocumentManager = () => {
  const BACKEND_URL = BACKEND_CONFIG.url;

  return {
    // Health check
    healthCheck: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/`);
        return await response.json();
      } catch (error) {
        console.error('Health check error:', error);
        return { status: 'offline' };
      }
    },

    // Research Management
    getResearch: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/research`);
        if (!response.ok) throw new Error(`Get research failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Get research error:', error);
        return [];
      }
    },

    createResearch: async (researchData) => {
      try {
        const response = await fetch(`${BACKEND_URL}/research`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(researchData)
        });
        if (!response.ok) throw new Error(`Create research failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Create research error:', error);
        throw error;
      }
    },

    updateResearch: async (id, researchData) => {
      try {
        const response = await fetch(`${BACKEND_URL}/research/${id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(researchData)
        });
        if (!response.ok) throw new Error(`Update research failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Update research error:', error);
        throw error;
      }
    },

    deleteResearch: async (id) => {
      try {
        const response = await fetch(`${BACKEND_URL}/research/${id}`, {
          method: 'DELETE'
        });
        if (!response.ok) throw new Error(`Delete research failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Delete research error:', error);
        throw error;
      }
    },

    // Creator Profile Management
    getProfiles: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/profiles`);
        if (!response.ok) throw new Error(`Get profiles failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Get profiles error:', error);
        return [];
      }
    },

    createProfile: async (profileData) => {
      try {
        const response = await fetch(`${BACKEND_URL}/profiles`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(profileData)
        });
        if (!response.ok) throw new Error(`Create profile failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Create profile error:', error);
        throw error;
      }
    },

    // Enhanced Content Generation
    generateContent: async (prompt, creatorKey, researchIds = []) => {
      try {
        const response = await fetch(`${BACKEND_URL}/content/generate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            prompt,
            creator_key: creatorKey,
            research_ids: researchIds
          })
        });
        if (!response.ok) throw new Error(`Content generation failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Content generation error:', error);
        throw error;
      }
    },

    // Document Management
    uploadDocument: async (file) => {
      try {
        const formData = new FormData();
        formData.append('file', file);

        const response = await fetch(`${BACKEND_URL}/upload`, {
          method: 'POST',
          body: formData
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.detail || `Upload failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Cloud upload error:', error);
        throw error;
      }
    },

    getDocuments: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/documents`);
        if (!response.ok) throw new Error(`Get documents failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Cloud get documents error:', error);
        throw error;
      }
    },

    deleteDocument: async (filename) => {
      try {
        const response = await fetch(`${BACKEND_URL}/documents/${encodeURIComponent(filename)}`, {
          method: 'DELETE'
        });
        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.detail || `Delete failed: ${response.status}`);
        }
        return await response.json();
      } catch (error) {
        console.error('Cloud delete error:', error);
        throw error;
      }
    },

    // Ask questions using backend
    askQuestion: async (question) => {
      try {
        const response = await fetch(`${BACKEND_URL}/ask`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ question })
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.detail || `Ask failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Cloud ask error:', error);
        throw error;
      }
    },

    // Website crawling
    crawlWebsite: async (url) => {
      try {
        const response = await fetch(`${BACKEND_URL}/crawl`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ url })
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.detail || `Crawl failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Cloud crawl error:', error);
        throw error;
      }
    },

    // Get statistics
    getStats: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/linkedin/stats`);
        if (!response.ok) throw new Error(`Get stats failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Get stats error:', error);
        return null;
      }
    },

    refreshIndex: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/refresh`, { method: 'POST' });
        if (!response.ok) throw new Error(`Refresh failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Refresh error:', error);
        throw error;
      }
    }
  };
};

// In-memory storage for configuration
let configStore = {
  openaiApiKey: '',
  model: 'gpt-4',
  supabaseUrl: '',
  supabaseKey: ''
};

const LinkedInContentBot = () => {
  const [activeTab, setActiveTab] = useState('chat');
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'bot',
      text: "Welcome to the LinkedIn Content Creator Assistant! I'm now connected to your cloud backend for enhanced document processing and content generation. How can I help you create engaging LinkedIn content today?",
      timestamp: new Date()
    }
  ]);

  const [isTyping, setIsTyping] = useState(false);
  const [showConfig, setShowConfig] = useState(false);
  const [config, setConfig] = useState(configStore);
  const [generatedContent, setGeneratedContent] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState({
    linkedinPost: 0,
    videoScript: 0
  });
  const messagesEndRef = useRef(null);

  // Backend connection state
  const [backendStatus, setBackendStatus] = useState('checking');
  const [backendError, setBackendError] = useState(null);
  const [documentManager, setDocumentManager] = useState(null);
  const [backendStats, setBackendStats] = useState(null);

  // Database states - now synced with backend
  const [researchDatabase, setResearchDatabase] = useState([]);
  const [creatorDatabase, setCreatorDatabase] = useState([]);
  const [customDocuments, setCustomDocuments] = useState([]);
  const [promptHistory, setPromptHistory] = useState([]);

  // Loading states
  const [isLoadingResearch, setIsLoadingResearch] = useState(false);
  const [isLoadingDocuments, setIsLoadingDocuments] = useState(false);
  const [isLoadingProfiles, setIsLoadingProfiles] = useState(false);

  // Form states
  const [editingResearch, setEditingResearch] = useState(null);
  const [editingCreator, setEditingCreator] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploadingDocument, setIsUploadingDocument] = useState(false);

  // Test backend connection and load data
  const initializeBackend = async () => {
    setBackendStatus('checking');
    setBackendError(null);

    const manager = createCloudDocumentManager();
    setDocumentManager(manager);

    try {
      const health = await manager.healthCheck();
      console.log('Backend health check:', health);

      if (health.status && health.status.includes('online')) {
        setBackendStatus('connected');

        // Load all data from backend
        await Promise.all([
          loadResearchFromBackend(manager),
          loadProfilesFromBackend(manager),
          loadDocumentsFromBackend(manager),
          loadStatsFromBackend(manager)
        ]);
      } else {
        throw new Error('Backend health check failed');
      }
    } catch (error) {
      console.error('Backend connection failed:', error);
      setBackendStatus('error');
      setBackendError(error.message);
    }
  };

  // Load functions for backend data
  const loadResearchFromBackend = async (manager = documentManager) => {
    if (!manager) return;

    setIsLoadingResearch(true);
    try {
      const research = await manager.getResearch();
      setResearchDatabase(research.map(item => ({
        ...item,
        tags: typeof item.tags === 'string' ? item.tags.split(',').map(t => t.trim()) : (item.tags || []),
        dateAdded: new Date(item.created_at || item.dateAdded || Date.now())
      })));
    } catch (error) {
      console.error('Error loading research from backend:', error);
    } finally {
      setIsLoadingResearch(false);
    }
  };

  const loadProfilesFromBackend = async (manager = documentManager) => {
    if (!manager) return;

    setIsLoadingProfiles(true);
    try {
      const profiles = await manager.getProfiles();
      setCreatorDatabase(profiles.map(profile => ({
        id: profile.id,
        name: profile.name,
        key: profile.key,
        icon: UserPlus, // Default icon
        style: {
          tone: profile.tone,
          structure: profile.structure,
          language: profile.language,
          length: profile.length,
          hooks: profile.hooks.split(',').map(h => h.trim()).filter(h => h),
          endings: profile.endings.split(',').map(e => e.trim()).filter(e => e),
          characteristics: profile.characteristics
        }
      })));
    } catch (error) {
      console.error('Error loading profiles from backend:', error);
      // Keep default profiles if backend fails
      setCreatorDatabase([
        {
          id: 1,
          name: "Professional Thought Leader",
          key: "thought_leader",
          icon: TrendingUp,
          style: {
            tone: 'Authoritative, insightful, forward-thinking',
            structure: 'Hook + insight + personal experience + call to action',
            language: 'Industry-specific terminology, confident assertions',
            length: 'Medium-length posts (800-1000 characters)',
            hooks: ['What if I told you...', 'The future of business...', 'Here\'s what most people miss...'],
            endings: ['What are your thoughts?', 'How do you see this evolving?', 'Share your perspective below'],
            characteristics: 'Data-driven, trend-focused, leadership-oriented'
          }
        }
      ]);
    } finally {
      setIsLoadingProfiles(false);
    }
  };

  const loadDocumentsFromBackend = async (manager = documentManager) => {
    if (!manager) return;

    setIsLoadingDocuments(true);
    try {
      const documents = await manager.getDocuments();
      setCustomDocuments(documents || []);
    } catch (error) {
      console.error('Error loading documents from backend:', error);
      setCustomDocuments([]);
    } finally {
      setIsLoadingDocuments(false);
    }
  };

  const loadStatsFromBackend = async (manager = documentManager) => {
    if (!manager) return;

    try {
      const stats = await manager.getStats();
      setBackendStats(stats);
    } catch (error) {
      console.error('Error loading stats from backend:', error);
    }
  };

  // Initialize on component mount
  useEffect(() => {
    initializeBackend();
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Enhanced content generation using backend
  const generateContentWithBackend = async (prompt, creatorKey, relevantResearchIds) => {
    if (!documentManager) {
      throw new Error('Backend not connected');
    }

    try {
      const content = await documentManager.generateContent(prompt, creatorKey, relevantResearchIds);

      // Save to prompt history
      const creator = creatorDatabase.find(c => c.key === creatorKey);
      setPromptHistory(prev => [{
        id: Date.now(),
        prompt,
        creator: creator?.name || creatorKey,
        research: content.research_used || [],
        timestamp: new Date(),
        response: {
          linkedinPosts: content.linkedin_posts,
          videoScripts: content.video_scripts,
          hashtags: content.hashtags,
          engagement_tips: content.engagement_tips
        },
        backendSources: [],
        creatorName: content.creator_name
      }, ...prev]);

      return {
        linkedinPosts: content.linkedin_posts,
        videoScripts: content.video_scripts,
        hashtags: content.hashtags,
        engagement_tips: content.engagement_tips
      };
    } catch (error) {
      console.error('Backend content generation error:', error);
      throw error;
    }
  };

  // Fallback OpenAI content generation
  const generateContentWithOpenAI = async (prompt, creatorStyle, relevantResearch) => {
    if (!config.openaiApiKey) {
      throw new Error('OpenAI API key not configured');
    }

    const systemPrompt = `You are a LinkedIn content creator that mimics the style of ${creatorStyle.name}. 

Style characteristics:
- Tone: ${creatorStyle.style.tone}
- Structure: ${creatorStyle.style.structure}
- Language: ${creatorStyle.style.language}
- Length: ${creatorStyle.style.length}
- Common hooks: ${creatorStyle.style.hooks.join(', ')}
- Common endings: ${creatorStyle.style.endings.join(', ')}
- Key characteristics: ${creatorStyle.style.characteristics}

Research data to incorporate:
${relevantResearch.map((r, index) => `
${index + 1}. Topic: ${r.topic}
   Findings: ${r.findings}
   Data: ${r.data}
   Source: ${r.source}
`).join('\n')}

Create TWO variations each for LinkedIn posts and video scripts that incorporate this research data in the creator's authentic style.

Return your response in this JSON format:
{
  "linkedinPosts": ["first post variation", "second post variation"],
  "videoScripts": ["first video script variation", "second video script variation"],
  "hashtags": ["array", "of", "hashtags"],
  "engagement_tips": ["array", "of", "tips"]
}`;

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${config.openaiApiKey}`
        },
        body: JSON.stringify({
          model: config.model,
          messages: [
            { role: 'system', content: systemPrompt },
            { role: 'user', content: prompt }
          ],
          temperature: 0.8,
          max_tokens: 3000
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`OpenAI API error: ${errorData.error?.message || response.status}`);
      }

      const data = await response.json();
      const content = JSON.parse(data.choices[0].message.content);

      // Save to prompt history
      setPromptHistory(prev => [{
        id: Date.now(),
        prompt,
        creator: creatorStyle.name,
        research: relevantResearch.map(r => r.topic),
        timestamp: new Date(),
        response: content
      }, ...prev]);

      return content;
    } catch (error) {
      console.error('OpenAI API error:', error);
      throw error;
    }
  };

  const findRelevantResearch = (text, maxResults = 5) => {
    const lowerText = text.toLowerCase();
    const searchTerms = lowerText.split(' ').filter(term => term.length > 2);

    const scoredResearch = researchDatabase.map(item => {
      let score = 0;
      const itemText = `${item.topic} ${item.findings} ${item.data} ${(Array.isArray(item.tags) ? item.tags.join(' ') : '')}`.toLowerCase();

      if (itemText.includes(lowerText)) score += 10;
      if (item.topic.toLowerCase().includes(lowerText)) score += 8;

      if (Array.isArray(item.tags)) {
        item.tags.forEach(tag => {
          if (lowerText.includes(tag.toLowerCase()) || tag.toLowerCase().includes(lowerText)) {
            score += 6;
          }
        });
      }

      searchTerms.forEach(term => {
        if (itemText.includes(term)) score += 2;
        if (item.topic.toLowerCase().includes(term)) score += 3;
      });

      return { ...item, relevanceScore: score };
    });

    return scoredResearch
      .sort((a, b) => b.relevanceScore - a.relevanceScore)
      .slice(0, maxResults);
  };

  const handleSendMessage = async (messageText) => {
    if (!messageText || !messageText.trim()) return;
    const text = messageText.trim();

    const userMessage = {
      id: Date.now(),
      type: 'user',
      text: text,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsTyping(true);
    setSelectedOptions({ linkedinPost: 0, videoScript: 0 });

    try {
      // Find creator style from text
      let creatorStyle = creatorDatabase.find(c => c.key === 'thought_leader') || creatorDatabase[0];

      const lowerText = text.toLowerCase();
      for (const creator of creatorDatabase) {
        if (lowerText.includes(creator.name.toLowerCase()) || 
            lowerText.includes(creator.key)) {
          creatorStyle = creator;
          break;
        }
      }

      if (!creatorStyle) {
        throw new Error('No creator profiles available. Please check your backend connection.');
      }

      const relevantResearch = findRelevantResearch(text, 5);
      const relevantResearchIds = relevantResearch.map(r => r.id);

      let content;

      // Try backend first, fallback to OpenAI if needed
      if (backendStatus === 'connected' && documentManager) {
        try {
          content = await generateContentWithBackend(text, creatorStyle.key, relevantResearchIds);
          console.log('Content generated using backend');
        } catch (backendError) {
          console.log('Backend generation failed, using OpenAI fallback:', backendError.message);
          if (!config.openaiApiKey) {
            throw new Error('Backend generation failed and OpenAI API key not configured. Please check your settings.');
          }
          content = await generateContentWithOpenAI(text, creatorStyle, relevantResearch);
        }
      } else {
        if (!config.openaiApiKey) {
          throw new Error('Backend not connected and OpenAI API key not configured. Please check your settings.');
        }
        content = await generateContentWithOpenAI(text, creatorStyle, relevantResearch);
      }

      setGeneratedContent(content);

      let responseText = `I've created 2 variations for each content type in ${creatorStyle.name}'s style.`;

      if (relevantResearch.length > 0) {
        responseText += ` I analyzed ${relevantResearch.length} research items: ${relevantResearch.map(r => r.topic).join(', ')}.`;
      }

      if (backendStatus === 'connected') {
        responseText += ` I also searched through your uploaded documents for relevant context.`;
      }

      responseText += ` Use the navigation arrows to choose your preferred options.`;

      const botResponse = {
        id: Date.now() + 1,
        type: 'bot',
        text: responseText,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, successMessage]);

    } catch (error) {
      console.error('Upload failed:', error);
      const errorMessage = {
        id: Date.now(),
        type: 'bot',
        text: `âŒ Upload failed: ${error.message}`,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsUploadingDocument(false);
    }
  };

