      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
        <div style={{ flex: 1, overflowY: 'auto', padding: '20px' }}>
          {messages.map((message) => (
            <div
              key={message.id}
              style={{
                display: 'flex',
                gap: '12px',
                marginBottom: '20px',
                alignItems: 'flex-start',
                flexDirection: message.type === 'user' ? 'row-reverse' : 'row'
              }}
            >
              <div style={{
                width: '32px',
                height: '32px',
                borderRadius: '50%',
                backgroundColor: message.type === 'bot' ? '#3b82f6' : '#10b981',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                flexShrink: 0
              }}>
                {message.type === 'bot' ? <Bot size={16} /> : <User size={16} />}
              </div>
              <div style={{
                maxWidth: '70%',
                backgroundColor: message.type === 'user' ? '#3b82f6' : 'white',
                color: message.type === 'user' ? 'white' : '#374151',
                padding: '12px 16px',
                borderRadius: '18px',
                border: message.type === 'bot' ? '1px solid #e5e7eb' : 'none',
                fontSize: '14px',
                lineHeight: '1.5'
              }}>
                <p style={{ margin: 0 }}>{message.text}</p>
                <div style={{
                  fontSize: '12px',
                  opacity: 0.7,
                  marginTop: '8px'
                }}>
                  {message.timestamp.toLocaleTimeString()}
                </div>
              </div>
            </div>
          ))}

          {generatedContent && <ContentDisplay content={generatedContent} />}

          {isTyping && (
            <div style={{
              display: 'flex',
              gap: '12px',
              marginBottom: '20px',
              alignItems: 'flex-start'
            }}>
              <div style={{
                width: '32px',
                height: '32px',
                borderRadius: '50%',
                backgroundColor: '#3b82f6',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white'
              }}>
                <Bot size={16} />
              </div>
              <div style={{
                backgroundColor: 'white',
                border: '1px solid #e5e7eb',
                padding: '12px 16px',
                borderRadius: '18px',
                display: 'flex',
                gap: '4px'
              }}>
                <div style={{
                  width: '8px',
                  height: '8px',
                  backgroundColor: '#9ca3af',
                  borderRadius: '50%',
                  animation: 'pulse 1.5s ease-in-out infinite'
                }}></div>
                <div style={{
                  width: '8px',
                  height: '8px',
                  backgroundColor: '#9ca3af',
                  borderRadius: '50%',
                  animation: 'pulse 1.5s ease-in-out 0.5s infinite'
                }}></div>
                <div style={{
                  width: '8px',
                  height: '8px',
                  backgroundColor: '#9ca3af',
                  borderRadius: '50%',
                  animation: 'pulse 1.5s ease-in-out 1s infinite'
                }}></div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        <div style={{ padding: '20px', borderTop: '1px solid #e5e7eb', backgroundColor: '#f9fafb' }}>
          <div style={{ display: 'flex', gap: '12px', alignItems: 'flex-end' }}>
            <textarea
              ref={textareaRef}
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me to create content in any creator's style using your research..."
              style={{
                flex: 1,
                minHeight: '44px',
                maxHeight: '120px',
                padding: '12px 16px',
                border: '1px solid #d1d5db',
                borderRadius: '22px',
                fontSize: '14px',
                resize: 'none',
                outline: 'none',
                backgroundColor: 'white'
              }}
              rows="1"
            />
            <button
              onClick={() => handleSendMessage()}
              disabled={!inputValue.trim() || isTyping}
              style={{
                width: '44px',
                height: '44px',
                backgroundColor: inputValue.trim() && !isTyping ? '#3b82f6' : '#9ca3af',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                cursor: inputValue.trim() && !isTyping ? 'pointer' : 'not-allowed',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flexShrink: 0
              }}
            >
              <Send size={16} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f3f4f6' }}>
      <div style={{
        width: '280px',
        backgroundColor: 'white',
        borderRight: '1px solid #e5e7eb',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <div style={{ padding: '24px', borderBottom: '1px solid #e5e7eb' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '24px' }}>
            <div style={{
              width: '40px',
              height: '40px',
              backgroundColor: '#3b82f6',
              borderRadius: '8px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <Linkedin size={24} style={{ color: 'white' }} />
            </div>
            <h1 style={{ margin: 0, fontSize: '18px', fontWeight: 'bold', color: '#1f2937' }}>Content Creator</h1>
          </div>

          <nav style={{ display: 'flex', flexDirection: 'column', gap: '4px' }}>
            <button
              onClick={() => setActiveTab('chat')}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '12px 16px',
                backgroundColor: activeTab === 'chat' ? '#eff6ff' : 'transparent',
                color: activeTab === 'chat' ? '#3b82f6' : '#6b7280',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '500',
                textAlign: 'left',
                width: '100%'
              }}
            >
              <Bot size={16} />
              <span>Chat</span>
            </button>
            <button
              onClick={() => setActiveTab('research')}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '12px 16px',
                backgroundColor: activeTab === 'research' ? '#eff6ff' : 'transparent',
                color: activeTab === 'research' ? '#3b82f6' : '#6b7280',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '500',
                textAlign: 'left',
                width: '100%'
              }}
            >
              <Database size={16} />
              <span>Research ({researchDatabase.length})</span>
            </button>
            <button
              onClick={() => setActiveTab('creators')}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '12px 16px',
                backgroundColor: activeTab === 'creators' ? '#eff6ff' : 'transparent',
                color: activeTab === 'creators' ? '#3b82f6' : '#6b7280',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '500',
                textAlign: 'left',
                width: '100%'
              }}
            >
              <Users size={16} />
              <span>Creators ({creatorDatabase.length})</span>
            </button>
            <button
              onClick={() => setActiveTab('history')}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '12px 16px',
                backgroundColor: activeTab === 'history' ? '#eff6ff' : 'transparent',
                color: activeTab === 'history' ? '#3b82f6' : '#6b7280',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '500',
                textAlign: 'left',
                width: '100%'
              }}
            >
              <BookOpen size={16} />
              <span>History ({promptHistory.length})</span>
            </button>
          </nav>
        </div>

        <div style={{ marginTop: 'auto', padding: '24px', borderTop: '1px solid #e5e7eb' }}>
          <button
            onClick={() => setShowConfig(true)}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px 16px',
              backgroundColor: 'transparent',
              color: '#6b7280',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '500',
              textAlign: 'left',
              width: '100%'
            }}
          >
            <Settings size={16} />
            <span>Settings</span>
          </button>
        </div>
      </div>

      <div style={{ flex: 1, overflow: 'hidden' }}>
        {activeTab === 'chat' && <ChatTab />}
        {activeTab === 'research' && <ResearchTab />}
        {activeTab === 'creators' && <CreatorTab />}
        {activeTab === 'history' && <HistoryTab />}
      </div>

      {showConfig && <ConfigPanel />}

      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        @keyframes pulse {
          0%, 80%, 100% { opacity: 0.3; }
          40% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default LinkedInContentBot;
