import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Linkedin, Video, FileText, Database, TrendingUp, Users, Lightbulb, Copy, Settings, Plus, Trash2, Edit3, Save, X, Search, BookOpen, UserPlus, ChevronLeft, ChevronRight, Upload, Globe, AlertCircle, CheckCircle, Clock } from 'lucide-react';

// ðŸ”§ CONFIGURATION - UPDATE THIS WITH YOUR BACKEND URL
const BACKEND_CONFIG = {
  // ðŸš¨ CHANGE THIS TO YOUR DEPLOYED BACKEND URL
  url: 'http://localhost:8000', // Replace with your Cloud Run URL

  // For local development, use: 'http://localhost:8000'
  // For production, use: 'https://linkedin-content-creator-api-YOUR_HASH-uc.a.run.app'
};

// Enhanced Cloud Document Manager with all backend endpoints
const createCloudDocumentManager = () => {
  const BACKEND_URL = BACKEND_CONFIG.url;

  return {
    // Health check
    healthCheck: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/`);
        if (!response.ok) throw new Error(`Health check failed: ${response.status}`);
        return await response.json();
      } catch (error) {
        console.error('Health check error:', error);
        throw error;
      }
    },

    // Research Management
    getResearch: async () => {
      const response = await fetch(`${BACKEND_URL}/research`);
      if (!response.ok) throw new Error(`Get research failed: ${response.status}`);
      return await response.json();
    },

    createResearch: async (researchData) => {
      const response = await fetch(`${BACKEND_URL}/research`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(researchData)
      });
      if (!response.ok) throw new Error(`Create research failed: ${response.status}`);
      return await response.json();
    },

    updateResearch: async (id, researchData) => {
      const response = await fetch(`${BACKEND_URL}/research/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(researchData)
      });
      if (!response.ok) throw new Error(`Update research failed: ${response.status}`);
      return await response.json();
    },

    deleteResearch: async (id) => {
      const response = await fetch(`${BACKEND_URL}/research/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error(`Delete research failed: ${response.status}`);
      return await response.json();
    },

    // Creator Profile Management
    getProfiles: async () => {
      const response = await fetch(`${BACKEND_URL}/profiles`);
      if (!response.ok) throw new Error(`Get profiles failed: ${response.status}`);
      return await response.json();
    },

    createProfile: async (profileData) => {
        const response = await fetch(`${BACKEND_URL}/profiles`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(profileData)
        });
        if (!response.ok) throw new Error(`Create profile failed: ${response.status}`);
        return await response.json();
    },

    updateProfile: async (id, profileData) => {
        const response = await fetch(`${BACKEND_URL}/profiles/${id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(profileData)
        });
        if (!response.ok) throw new Error(`Update profile failed: ${response.status}`);
        return await response.json();
    },

    deleteProfile: async (id) => {
        const response = await fetch(`${BACKEND_URL}/profiles/${id}`, {
            method: 'DELETE'
        });
        if (!response.ok) throw new Error(`Delete profile failed: ${response.status}`);
        return await response.json();
    },

    // Content Generation
    generateContent: async (prompt, creatorKey, researchIds = []) => {
      const response = await fetch(`${BACKEND_URL}/content/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          prompt,
          creator_key: creatorKey,
          research_ids: researchIds
        })
      });
      if (!response.ok) throw new Error(`Content generation failed: ${response.status}`);
      return await response.json();
    },

    // Document Management
    uploadDocument: async (file) => {
        const formData = new FormData();
        formData.append('file', file);
        const response = await fetch(`${BACKEND_URL}/upload`, {
            method: 'POST',
            body: formData
        });
        if (!response.ok) {
            const errorData = await response.json().catch(() => ({}));
            throw new Error(errorData.detail || `Upload failed: ${response.status}`);
        }
        return await response.json();
    },

    getDocuments: async () => {
        const response = await fetch(`${BACKEND_URL}/documents`);
        if (!response.ok) throw new Error(`Get documents failed: ${response.status}`);
        return await response.json();
    },

    deleteDocument: async (filename) => {
        const response = await fetch(`${BACKEND_URL}/documents/${encodeURIComponent(filename)}`, {
            method: 'DELETE'
        });
        if (!response.ok) {
            const errorData = await response.json().catch(() => ({}));
            throw new Error(errorData.detail || `Delete failed: ${response.status}`);
        }
        return await response.json();
    },

    // Website crawling
    crawlWebsite: async (url) => {
        const response = await fetch(`${BACKEND_URL}/crawl`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ url })
        });
        if (!response.ok) {
            const errorData = await response.json().catch(() => ({}));
            throw new Error(errorData.detail || `Crawl failed: ${response.status}`);
        }
        return await response.json();
    },

    // Get statistics
    getStats: async () => {
        const response = await fetch(`${BACKEND_URL}/linkedin/stats`);
        if (!response.ok) throw new Error(`Get stats failed: ${response.status}`);
        return await response.json();
    },

    refreshIndex: async () => {
        const response = await fetch(`${BACKEND_URL}/refresh`, { method: 'POST' });
        if (!response.ok) throw new Error(`Refresh failed: ${response.status}`);
        return await response.json();
    }
  };
};

// In-memory storage for configuration
let configStore = {
  openaiApiKey: '',
  model: 'gpt-4',
};

const LinkedInContentBot = () => {
  const [activeTab, setActiveTab] = useState('chat');
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'bot',
      text: "Welcome to the LinkedIn Content Creator Assistant! I'll try to connect to your cloud backend for enhanced features. How can I help you create engaging content today?",
      timestamp: new Date()
    }
  ]);

  const [isTyping, setIsTyping] = useState(false);
  const [showConfig, setShowConfig] = useState(false);
  const [config, setConfig] = useState(configStore);
  const [generatedContent, setGeneratedContent] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState({
    linkedinPost: 0,
    videoScript: 0
  });
  const messagesEndRef = useRef(null);

  // Backend connection state
  const [backendStatus, setBackendStatus] = useState('checking');
  const [backendError, setBackendError] = useState(null);
  const [documentManager, setDocumentManager] = useState(null);
  const [backendStats, setBackendStats] = useState(null);

  // Database states - now synced with backend
  const [researchDatabase, setResearchDatabase] = useState([]);
  const [creatorDatabase, setCreatorDatabase] = useState([]);
  const [customDocuments, setCustomDocuments] = useState([]);
  const [promptHistory, setPromptHistory] = useState([]);

  // Loading states
  const [isLoadingResearch, setIsLoadingResearch] = useState(false);
  const [isLoadingDocuments, setIsLoadingDocuments] = useState(false);
  const [isLoadingProfiles, setIsLoadingProfiles] = useState(false);

  // Form states
  const [editingResearch, setEditingResearch] = useState(null);
  const [editingCreator, setEditingCreator] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploadingDocument, setIsUploadingDocument] = useState(false);

  // Load functions for backend data
    const loadResearchFromBackend = async (manager = documentManager) => {
        if (!manager) return;
        setIsLoadingResearch(true);
        try {
            const research = await manager.getResearch();
            setResearchDatabase(research.map(item => ({
                ...item,
                tags: typeof item.tags === 'string' ? item.tags.split(',').map(t => t.trim()) : (item.tags || []),
                dateAdded: new Date(item.created_at || item.dateAdded || Date.now())
            })));
        } catch (error) {
            console.error('Error loading research from backend:', error);
        } finally {
            setIsLoadingResearch(false);
        }
    };

    const loadProfilesFromBackend = async (manager = documentManager) => {
        if (!manager) return;
        setIsLoadingProfiles(true);
        try {
            const profiles = await manager.getProfiles();
            setCreatorDatabase(profiles.map(profile => ({
                id: profile.id,
                name: profile.name,
                key: profile.key,
                icon: UserPlus, // Default icon
                style: {
                    tone: profile.tone,
                    structure: profile.structure,
                    language: profile.language,
                    length: profile.length,
                    hooks: Array.isArray(profile.hooks) ? profile.hooks : (profile.hooks || '').split(',').map(h => h.trim()).filter(h => h),
                    endings: Array.isArray(profile.endings) ? profile.endings : (profile.endings || '').split(',').map(e => e.trim()).filter(e => e),
                    characteristics: profile.characteristics
                }
            })));
        } catch (error) {
            console.error('Error loading profiles from backend:', error);
        } finally {
            setIsLoadingProfiles(false);
        }
    };

    const loadDocumentsFromBackend = async (manager = documentManager) => {
        if (!manager) return;
        setIsLoadingDocuments(true);
        try {
            const documents = await manager.getDocuments();
            setCustomDocuments(documents || []);
        } catch (error) {
            console.error('Error loading documents from backend:', error);
            setCustomDocuments([]);
        } finally {
            setIsLoadingDocuments(false);
        }
    };

    const loadStatsFromBackend = async (manager = documentManager) => {
        if (!manager) return;
        try {
            const stats = await manager.getStats();
            setBackendStats(stats);
        } catch (error) {
            console.error('Error loading stats from backend:', error);
        }
    };

    const initializeBackend = async () => {
        setBackendStatus('checking');
        setBackendError(null);
        const manager = createCloudDocumentManager();
        setDocumentManager(manager);
        try {
            const health = await manager.healthCheck();
            if (health.status && health.status.includes('online')) {
                setBackendStatus('connected');
                await Promise.all([
                    loadResearchFromBackend(manager),
                    loadProfilesFromBackend(manager),
                    loadDocumentsFromBackend(manager),
                    loadStatsFromBackend(manager)
                ]);
            } else {
                throw new Error('Backend health check failed');
            }
        } catch (error) {
            console.error('Backend connection failed:', error);
            setBackendStatus('error');
            setBackendError(error.message);
        }
    };

  // Initialize on component mount
  useEffect(() => {
    initializeBackend();
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const addMessageToChat = (text, type = 'bot') => {
      setMessages(prev => [...prev, {
          id: Date.now(),
          type,
          text,
          timestamp: new Date()
      }]);
  };

  const handleSendMessage = async (messageText) => {
    // ... main message handling logic (omitted for brevity, same as in your code)
  };

  const handleCreatorSelect = (creatorKey) => {
      const creator = creatorDatabase.find(c => c.key === creatorKey);
      if (creator) {
          const message = `Create a ${creator.name} style LinkedIn post`;
          setMessages(prev => [...prev, { id: Date.now(), type: 'user', text: message, timestamp: new Date() }]);
          // You can also trigger the actual content generation here
      }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    addMessageToChat('âœ… Content copied to clipboard!');
  };

  // Research management functions
    const handleAddResearch = async (researchData) => {
        if (!researchData.topic.trim()) return;
        try {
            if (backendStatus === 'connected' && documentManager) {
                await documentManager.createResearch({
                    topic: researchData.topic,
                    findings: researchData.findings,
                    source: researchData.source,
                    data: researchData.data,
                    tags: researchData.tags
                });
                await loadResearchFromBackend();
                addMessageToChat(`âœ… Research on "${researchData.topic}" added successfully.`);
            } else {
                throw new Error("Backend not connected. Cannot add research.");
            }
        } catch (error) {
            console.error('Error adding research:', error);
            addMessageToChat(`âŒ Error: ${error.message}`);
        }
    };

    const handleDeleteResearch = async (id) => {
        try {
            if (backendStatus === 'connected' && documentManager) {
                await documentManager.deleteResearch(id);
                await loadResearchFromBackend();
                addMessageToChat(`âœ… Research item deleted.`);
            } else {
                 throw new Error("Backend not connected. Cannot delete research.");
            }
        } catch (error) {
            console.error('Error deleting research:', error);
            addMessageToChat(`âŒ Error: ${error.message}`);
        }
    };

    const handleUpdateResearch = async (id, updatedData) => {
        try {
            if (backendStatus === 'connected' && documentManager) {
                await documentManager.updateResearch(id, {
                    ...updatedData,
                    tags: typeof updatedData.tags === 'string' ? updatedData.tags : updatedData.tags.join(',')
                });
                await loadResearchFromBackend();
                setEditingResearch(null);
                addMessageToChat(`âœ… Research on "${updatedData.topic}" updated.`);
            } else {
                 throw new Error("Backend not connected. Cannot update research.");
            }
        } catch (error) {
            console.error('Error updating research:', error);
            addMessageToChat(`âŒ Error: ${error.message}`);
            setEditingResearch(null);
        }
    };

    // Creator Profile Management Functions
    const handleUpdateCreator = async (id, updatedData) => {
        try {
            if (backendStatus === 'connected' && documentManager) {
                const payload = {
                    name: updatedData.name,
                    tone: updatedData.tone,
                    structure: updatedData.structure,
                    language: updatedData.language,
                    length: updatedData.length,
                    hooks: updatedData.hooks.join(','),
                    endings: updatedData.endings.join(','),
                    characteristics: updatedData.characteristics
                };
                await documentManager.updateProfile(id, payload);
                await loadProfilesFromBackend();
                addMessageToChat(`âœ… Creator "${updatedData.name}" has been updated.`);
            } else {
                throw new Error("Backend not connected. Cannot update creator.");
            }
        } catch (error) {
            console.error('Error updating creator:', error);
            addMessageToChat(`âŒ Error: ${error.message}`);
        } finally {
            setEditingCreator(null);
        }
    };

    const handleDeleteCreator = async (creator) => {
        try {
            if (backendStatus === 'connected' && documentManager) {
                await documentManager.deleteProfile(creator.id);
                await loadProfilesFromBackend();
                addMessageToChat(`âœ… Creator "${creator.name}" has been deleted.`);
            } else {
                throw new Error("Backend not connected. Cannot delete creator.");
            }
        } catch (error) {
            console.error('Error deleting creator:', error);
            addMessageToChat(`âŒ Error: ${error.message}`);
        }
    };


  // Document management functions
    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        if (file) setSelectedFile(file);
    };

    const handleUploadDocument = async () => {
        if (!selectedFile || !documentManager) return;
        setIsUploadingDocument(true);
        try {
            const result = await documentManager.uploadDocument(selectedFile);
            await loadDocumentsFromBackend();
            await loadStatsFromBackend();
            setSelectedFile(null);
            document.getElementById('file-input').value = '';
            addMessageToChat(`âœ… Successfully uploaded "${result.filename}". You can now query its content.`);
        } catch (error) {
            console.error('Upload failed:', error);
            addMessageToChat(`âŒ Upload failed: ${error.message}`);
        } finally {
            setIsUploadingDocument(false);
        }
    };

    const handleDeleteDocument = async (filename) => {
        if (!documentManager) return;
        try {
            await documentManager.deleteDocument(filename);
            await loadDocumentsFromBackend();
            await loadStatsFromBackend();
            addMessageToChat(`âœ… Successfully deleted "${filename}" and updated the search index.`);
        } catch (error) {
            console.error('Delete failed:', error);
            addMessageToChat(`âŒ Delete failed: ${error.message}`);
        }
    };

    const handleCrawlWebsite = async (url) => {
        if (!documentManager || !url) return;
        setIsCrawling(true);
        try {
            const result = await documentManager.crawlWebsite(url);
            await loadDocumentsFromBackend();
            await loadStatsFromBackend();
            addMessageToChat(`âœ… Successfully crawled "${result.title || url}". You can now query its content.`);
        } catch (error) {
            console.error('Crawl failed:', error);
            addMessageToChat(`âŒ Crawl failed: ${error.message}`);
        } finally {
             setIsCrawling(false);
        }
    };

  // --- SUB-COMPONENTS ---

    const BackendStatus = () => (
        <div className={`p-3 rounded-lg mb-4 flex items-center gap-3 text-sm ${
            backendStatus === 'connected' ? 'bg-green-50 border border-green-200 text-green-800' :
            backendStatus === 'error' ? 'bg-red-50 border border-red-200 text-red-800' :
            'bg-yellow-50 border border-yellow-200 text-yellow-800'
        }`}>
            {backendStatus === 'connected' && <CheckCircle className="w-5 h-5" />}
            {backendStatus === 'error' && <AlertCircle className="w-5 h-5" />}
            {backendStatus === 'checking' && <Clock className="w-5 h-5 animate-spin" />}

            <div className="flex-1">
                <p className="font-medium">
                    Backend: {backendStatus.charAt(0).toUpperCase() + backendStatus.slice(1)}
                </p>
                {backendError && <p className="text-xs">Error: {backendError}</p>}
            </div>
             {backendStatus === 'error' && (
                <button onClick={initializeBackend} className="px-3 py-1 bg-red-100 text-red-700 rounded-md text-xs font-semibold hover:bg-red-200">
                    Retry
                </button>
            )}
        </div>
    );

    const CreatorEditForm = ({ creator, onSave, onCancel }) => {
        const [editForm, setEditForm] = useState({
            name: creator.name,
            tone: creator.style.tone,
            structure: creator.style.structure,
            language: creator.style.language,
            length: creator.style.length,
            hooks: (creator.style.hooks || []).join(', '),
            endings: (creator.style.endings || []).join(', '),
            characteristics: creator.style.characteristics
        });

        const handleEditChange = (field, value) => {
            setEditForm(prev => ({ ...prev, [field]: value }));
        };

        const handleSave = () => {
            const updatedData = {
                ...editForm,
                hooks: editForm.hooks.split(',').map(h => h.trim()).filter(h => h),
                endings: editForm.endings.split(',').map(e => e.trim()).filter(e => e),
            };
            onSave(creator.id, updatedData);
        };

        return (
            <div className="space-y-4">
                 <input type="text" value={editForm.name} onChange={(e) => handleEditChange('name', e.target.value)} placeholder="Creator Name" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <input type="text" value={editForm.tone} onChange={(e) => handleEditChange('tone', e.target.value)} placeholder="Tone" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <input type="text" value={editForm.structure} onChange={(e) => handleEditChange('structure', e.target.value)} placeholder="Structure" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <input type="text" value={editForm.language} onChange={(e) => handleEditChange('language', e.target.value)} placeholder="Language" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <input type="text" value={editForm.hooks} onChange={(e) => handleEditChange('hooks', e.target.value)} placeholder="Hooks (comma-separated)" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <input type="text" value={editForm.endings} onChange={(e) => handleEditChange('endings', e.target.value)} placeholder="Endings (comma-separated)" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                 <textarea value={editForm.characteristics} onChange={(e) => handleEditChange('characteristics', e.target.value)} placeholder="Characteristics" className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm resize-none" rows="3" />
                <div className="flex gap-2">
                    <button onClick={handleSave} className="px-4 py-2 bg-green-600 text-white rounded-lg text-sm font-medium hover:bg-green-700 flex items-center gap-2"><Save className="w-4 h-4" /> Save</button>
                    <button onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded-lg text-sm font-medium hover:bg-gray-700 flex items-center gap-2"><X className="w-4 h-4" /> Cancel</button>
                </div>
            </div>
        );
    };

    const CreatorTab = () => {
        const [localNewCreator, setLocalNewCreator] = useState({
            name: '', key: '', tone: '', structure: '', language: '', length: '', hooks: '', endings: '', characteristics: ''
        });

        const handleLocalCreatorChange = (field, value) => {
            setLocalNewCreator(prev => ({ 
                ...prev, 
                [field]: value,
                ...(field === 'name' && { key: value.toLowerCase().replace(/\s+/g, '_').replace(/[^a-z0-9_]/g, '') })
            }));
        };

        const handleAddCreator = async () => {
            if (!localNewCreator.name.trim() || backendStatus !== 'connected') return;
            try {
                await documentManager.createProfile({
                    name: localNewCreator.name,
                    key: localNewCreator.key,
                    tone: localNewCreator.tone,
                    structure: localNewCreator.structure,
                    language: localNewCreator.language,
                    length: localNewCreator.length,
                    hooks: localNewCreator.hooks,
                    endings: localNewCreator.endings,
                    characteristics: localNewCreator.characteristics
                });
                await loadProfilesFromBackend();
                addMessageToChat(`âœ… Creator "${localNewCreator.name}" added.`);
                setLocalNewCreator({ name: '', key: '', tone: '', structure: '', language: '', length: '', hooks: '', endings: '', characteristics: '' });
            } catch (error) {
                console.error('Failed to add creator:', error);
                addMessageToChat(`âŒ Error adding creator: ${error.message}`);
            }
        };

        return (
            <div className="flex-1 overflow-y-auto bg-gray-50 p-6">
                <h2 className="text-xl font-semibold text-gray-900 mb-6">Creator Profiles</h2>
                <BackendStatus />
                <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200 mb-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">Add New Creator</h3>
                     {/* Form inputs for new creator... */}
                     <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                        <input type="text" placeholder="Creator Name" value={localNewCreator.name} onChange={(e) => handleLocalCreatorChange('name', e.target.value)} className="px-3 py-2 border rounded-lg text-sm" />
                        <input type="text" placeholder="Key (auto-generated)" value={localNewCreator.key} readOnly className="px-3 py-2 border rounded-lg text-sm bg-gray-100" />
                        <input type="text" placeholder="Tone" value={localNewCreator.tone} onChange={(e) => handleLocalCreatorChange('tone', e.target.value)} className="px-3 py-2 border rounded-lg text-sm" />
                        <input type="text" placeholder="Structure" value={localNewCreator.structure} onChange={(e) => handleLocalCreatorChange('structure', e.target.value)} className="px-3 py-2 border rounded-lg text-sm" />
                        <input type="text" placeholder="Language" value={localNewCreator.language} onChange={(e) => handleLocalCreatorChange('language', e.target.value)} className="px-3 py-2 border rounded-lg text-sm" />
                        <input type="text" placeholder="Hooks (comma-separated)" value={localNewCreator.hooks} onChange={(e) => handleLocalCreatorChange('hooks', e.target.value)} className="px-3 py-2 border rounded-lg text-sm" />
                     </div>
                     <textarea placeholder="Key Characteristics" value={localNewCreator.characteristics} onChange={(e) => handleLocalCreatorChange('characteristics', e.target.value)} className="w-full px-3 py-2 border rounded-lg text-sm mb-4" rows="3" />
                    <button onClick={handleAddCreator} disabled={!localNewCreator.name.trim() || backendStatus !== 'connected'} className="px-6 py-2 bg-gray-900 text-white rounded-lg text-sm font-medium hover:bg-gray-800 disabled:opacity-50 flex items-center gap-2">
                        <Plus className="w-4 h-4" /> Add Creator
                    </button>
                </div>

                {isLoadingProfiles && <div className="text-center p-8"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div></div>}

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {creatorDatabase.map((creator) => (
                        <div key={creator.id} className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
                            {editingCreator === creator.id ? (
                                <CreatorEditForm creator={creator} onSave={handleUpdateCreator} onCancel={() => setEditingCreator(null)} />
                            ) : (
                                <div>
                                    <div className="flex justify-between items-start mb-4">
                                        <div className="flex items-center gap-3">
                                            <creator.icon className="w-6 h-6 text-gray-600" />
                                            <div>
                                                <h3 className="text-lg font-medium text-gray-900">{creator.name}</h3>
                                                <p className="text-xs text-gray-500">{creator.key}</p>
                                            </div>
                                        </div>
                                        <div className="flex gap-2">
                                            <button onClick={() => setEditingCreator(creator.id)} className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg"><Edit3 className="w-4 h-4" /></button>
                                            <button onClick={() => handleDeleteCreator(creator)} className="p-2 text-red-600 hover:bg-red-50 rounded-lg"><Trash2 className="w-4 h-4" /></button>
                                        </div>
                                    </div>
                                    <div className="space-y-3 text-sm">
                                        <div><span className="font-medium text-gray-700">Tone:</span><span className="ml-2 text-gray-600">{creator.style.tone}</span></div>
                                        <div><span className="font-medium text-gray-700">Structure:</span><span className="ml-2 text-gray-600">{creator.style.structure}</span></div>
                                        <div><span className="font-medium text-gray-700">Language:</span><span className="ml-2 text-gray-600">{creator.style.language}</span></div>
                                        <div><span className="font-medium text-gray-700">Hooks:</span>
                                            <div className="mt-1 flex flex-wrap gap-1">{(creator.style.hooks || []).map((hook, index) => (<span key={index} className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">{hook}</span>))}</div>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    const CustomDocumentsTab = () => {
        const [crawlUrl, setCrawlUrl] = useState('');
        const [isCrawling, setIsCrawling] = useState(false);
        // ... rest of the component logic ...
        return (
             <div className="flex-1 overflow-y-auto bg-gray-50 p-6">
                <h2 className="text-xl font-semibold text-gray-900 mb-6">Cloud Document Management</h2>
                <BackendStatus />

                {/* Upload Document */}
                <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200 mb-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">Upload Document</h3>
                    <div className="flex gap-4 items-end">
                        <div className="flex-1">
                            <input id="file-input" type="file" accept=".pdf,.docx,.xlsx,.xls,.pptx,.ppt" onChange={handleFileSelect} className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                            <p className="text-xs text-gray-500 mt-1">Supported: PDF, DOCX, XLSX, PPTX</p>
                        </div>
                        <button onClick={handleUploadDocument} disabled={!selectedFile || isUploadingDocument || backendStatus !== 'connected'} className="px-6 py-2 bg-blue-600 text-white rounded-lg text-sm font-medium hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2">
                            {isUploadingDocument ? <><div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div> Uploading...</> : <><Upload className="w-4 h-4" /> Upload</>}
                        </button>
                    </div>
                </div>

                {/* Crawl Website */}
                <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200 mb-6">
                     <h3 className="text-lg font-medium text-gray-900 mb-4">Crawl Website</h3>
                     <div className="flex gap-4 items-end">
                         <div className="flex-1">
                             <input type="url" placeholder="https://example.com" value={crawlUrl} onChange={(e) => setCrawlUrl(e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm" />
                             <p className="text-xs text-gray-500 mt-1">Extract content from any public website.</p>
                         </div>
                         <button onClick={() => handleCrawlWebsite(crawlUrl).then(() => setCrawlUrl(''))} disabled={!crawlUrl || isCrawling || backendStatus !== 'connected'} className="px-6 py-2 bg-green-600 text-white rounded-lg text-sm font-medium hover:bg-green-700 disabled:opacity-50 flex items-center gap-2">
                            {isCrawling ? <><div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div> Crawling...</> : <><Globe className="w-4 h-4" /> Crawl</>}
                         </button>
                     </div>
                </div>

                {/* Indexed Documents */}
                <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
                     <h3 className="text-lg font-medium text-gray-900 mb-4">Indexed Documents</h3>
                     {isLoadingDocuments ? (
                         <div className="text-center p-8"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div></div>
                     ) : customDocuments.length === 0 ? (
                         <div className="text-center py-12"><Database className="w-12 h-12 text-gray-400 mx-auto mb-4" /><p className="text-gray-600">No documents indexed yet.</p></div>
                     ) : (
                         <div className="space-y-4">
                             {customDocuments.map((doc, index) => (
                                 <div key={doc.filename || index} className="flex justify-between items-center p-4 border rounded-lg">
                                     <div>
                                         <h4 className="font-medium text-gray-900">{doc.filename}</h4>
                                         <p className="text-sm text-gray-500">{doc.total_chunks} chunks</p>
                                     </div>
                                     <button onClick={() => handleDeleteDocument(doc.filename)} className="p-2 text-red-600 hover:bg-red-50 rounded-lg"><Trash2 className="w-4 h-4" /></button>
                                 </div>
                             ))}
                         </div>
                     )}
                </div>
            </div>
        );
    };

    const HistoryTab = () => (
         <div className="flex-1 overflow-y-auto bg-gray-50 p-6">
             <h2 className="text-xl font-semibold text-gray-900 mb-6">Prompt History</h2>
             <div className="space-y-4">
                 {promptHistory.length === 0 && <div className="text-center py-12 bg-white rounded-lg border"><BookOpen className="w-12 h-12 text-gray-400 mx-auto mb-4" /><p className="text-gray-600">No prompts generated yet.</p></div>}
                 {promptHistory.map((entry) => (
                     <div key={entry.id} className="bg-white rounded-lg p-6 shadow-sm border">
                         {/* History entry display logic... */}
                     </div>
                 ))}
             </div>
         </div>
    );

    const ChatTab = () => (
        <>
            <div className="bg-gray-50 border-b p-6">
                <h3 className="text-sm font-semibold text-gray-700 uppercase tracking-wide mb-3">Quick Actions</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-3">
                    {creatorDatabase.slice(0, 4).map((creator) => (
                        <button key={creator.key} onClick={() => handleCreatorSelect(creator.key)} className="flex items-center gap-3 p-3.5 bg-white border rounded-lg hover:shadow-md transition-all text-left">
                            <Users className="w-5 h-5 text-gray-600" />
                            <span className="text-sm font-medium text-gray-700">{creator.name}</span>
                        </button>
                    ))}
                </div>
            </div>
            <div className="flex-1 overflow-y-auto p-6 bg-white" ref={messagesEndRef}>
                <div className="space-y-6">
                    {messages.map((message) => (
                        <div key={message.id} className={`flex w-full ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                           {/* Message bubble logic */}
                        </div>
                    ))}
                    {isTyping && <div className="flex items-center gap-1">...</div>}
                </div>
            </div>
            <div className="border-t p-4 bg-white">
                <div className="flex items-end gap-3">
                    <textarea placeholder="Ask me to create content..." className="w-full px-4 py-3 border rounded-lg text-sm resize-none" rows="1" />
                    <button className="p-3 bg-gray-900 text-white rounded-lg hover:bg-gray-800"><Send className="w-4 h-4" /></button>
                </div>
            </div>
        </>
    );

  // --- MAIN RENDER ---
  return (
    <div className="flex h-screen bg-gray-100 font-sans">
      <div className="w-72 bg-white border-r border-gray-200 flex flex-col">
        <div className="p-6 border-b">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
              <Linkedin className="w-5 h-5 text-white" />
            </div>
            <h1 className="text-xl font-bold text-gray-900">Content AI</h1>
          </div>
        </div>

        <nav className="flex-1 p-4 space-y-2">
            {[
                { key: 'chat', label: 'Chat', icon: Bot, count: messages.length -1 },
                { key: 'creators', label: 'Creators', icon: Users, count: creatorDatabase.length },
                { key: 'documents', label: 'Documents', icon: FileText, count: customDocuments.length },
                { key: 'history', label: 'History', icon: BookOpen, count: promptHistory.length },
            ].map(tab => {
                const Icon = tab.icon;
                return (
                    <button key={tab.key} onClick={() => setActiveTab(tab.key)}
                        className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-lg text-sm font-medium transition-colors ${
                            activeTab === tab.key ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                        }`}
                    >
                        <Icon className="w-4 h-4" />
                        <span>{tab.label}</span>
                        <span className={`ml-auto text-xs font-mono px-2 py-0.5 rounded-full ${
                            activeTab === tab.key ? 'bg-white/20 text-white' : 'bg-gray-200 text-gray-600'
                        }`}>{tab.count}</span>
                    </button>
                )
            })}
        </nav>

        <div className="p-4 border-t">
          <button onClick={() => setShowConfig(true)} className="w-full flex items-center gap-3 px-3 py-2.5 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-100">
            <Settings className="w-4 h-4" /> Settings
          </button>
        </div>
      </div>

      <main className="flex-1 flex flex-col">
        {activeTab === 'chat' && <ChatTab />}
        {activeTab === 'creators' && <CreatorTab />}
        {activeTab === 'documents' && <CustomDocumentsTab />}
        {activeTab === 'history' && <HistoryTab />}
        {/* ResearchTab is not in the sidebar but could be added if needed */}
        {/* {activeTab === 'research' && <ResearchTab />} */}
      </main>

      {showConfig && <div className="fixed inset-0 bg-black/40 z-50">...Config Panel...</div>}
    </div>
  );
};

export default LinkedInContentBot;
