    crawlWebsite: async (url) => {
      try {
        console.log('Crawling website:', url);

        // Clean the URL to prevent JSON parsing issues
        const cleanUrl = url.trim().replace(/[^\w\-._~:/?#[\]@!$&'()*+,;=%]/g, '');

        // Use the backend's crawl endpoint to extract real content
        const response = await fetch(`${BACKEND_URL}/crawl`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ url: cleanUrl })
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Crawl response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Crawl failed: ${response.status}`);
        }

        const crawlResult = await response.json();
        console.log('Crawl result:', crawlResult);

        // Extract meaningful content from the crawled data
        let extractedContent = '';
        let title = '';

        if (crawlResult.main_text) {
          // Clean and extract the main content
          extractedContent = crawlResult.main_text
            .replace(/\s+/g, ' ')
            .trim()
            .substring(0, 2000); // Limit to first 2000 characters
        }

        if (crawlResult.title) {
          title = crawlResult.title;
        } else {
          title = `Content from ${new URL(cleanUrl).hostname}`;
        }

        // Create a comprehensive research item with the actual crawled content
        const researchItem = {
          topic: title,
          findings: extractedContent || `Content extracted from ${cleanUrl}`,
          data: `Source URL: ${cleanUrl}\nHostname: ${crawlResult.hostname || new URL(cleanUrl).hostname}\nExtracted on: ${new Date().toISOString()}`,
          source: cleanUrl,
          tags: `web-crawl,${crawlResult.hostname || 'website'},content-extraction`
        };

        console.log('Creating research item for crawled content:', researchItem);

        // Add to research via backend
        const researchResponse = await fetch(`${BACKEND_URL}/linkedin/research`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(researchItem)
        });

        if (!researchResponse.ok) {
          const errorData = await researchResponse.json().catch(() => ({}));
          console.error('Research creation error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Failed to add crawled content: ${researchResponse.status}`);
        }

        const result = await researchResponse.json();
        console.log('Crawled content added successfully:', result);

        return {
          title: title,
          filename: `Web Content - ${cleanUrl}`,
          chunks_added: 1,
          crawlData: {
            title: crawlResult.title,
            hostname: crawlResult.hostname,
            contentLength: extractedContent.length,
            source: cleanUrl
          }
        };
      } catch (error) {
        console.error('Enhanced crawl error:', error);
        throw error;
      }
    },

    // Get all chunks from backend
    getAllChunks: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/chunks`);

        if (!response.ok) {
          throw new Error(`Get chunks failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Get chunks error:', error);
        return [];
      }
    },

    // Refresh index
    refreshIndex: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/refresh`, {
          method: 'POST'
        });

        if (!response.ok) {
          throw new Error(`Refresh failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Refresh error:', error);
        throw error;
      }
    },

    // Health check
    healthCheck: async () => {
      try {
