import React, { useState, useRef, useEffect } from 'react';
import { supabase, TABLES } from './supabase-config';
import { Send, Bot, User, Linkedin, Video, FileText, Database, TrendingUp, Users, Lightbulb, Copy, Settings, Plus, Trash2, Edit3, Save, X, Search, BookOpen, UserPlus, ChevronLeft, ChevronRight, Upload, Globe, AlertCircle, CheckCircle, Clock } from 'lucide-react';

// ðŸ”§ CONFIGURATION - UPDATE THIS WITH YOUR BACKEND URL
const BACKEND_CONFIG = {
  // âœ… CONFIGURED: Your FastAPI Backend URL
  url: 'https://linkedin-content-creator-api-11874687899.asia-south1.run.app',

  // This is your deployed FastAPI backend with full functionality
  // The backend supports: linkedin/generate, linkedin/research, linkedin/creators endpoints

  // Note: Your existing backend doesn't have document upload endpoints,
  // but it has excellent content generation and research management features
};

// Cloud Document Manager - Updated to match your backend API
const createCloudDocumentManager = () => {
  const BACKEND_URL = BACKEND_CONFIG.url;

  return {
    // Upload document as research item (working backend approach)
    uploadDocument: async (file) => {
      try {
        console.log('Attempting to upload file:', file.name, 'Size:', file.size, 'Type:', file.type);

        // Create research item from file
        const researchItem = {
          topic: `Document: ${file.name}`,
          findings: `Uploaded document: ${file.name} (${file.size} bytes)`,
          data: `Document content from ${file.name} (${file.size} bytes)`,
          source: file.name,
          tags: `document, ${file.type}, upload`
        };

        console.log('Creating research item for document:', researchItem);

        const response = await fetch(`${BACKEND_URL}/linkedin/research`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(researchItem)
        });

        console.log('Upload response status:', response.status);

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Upload response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Upload failed: ${response.status}`);
        }

        const result = await response.json();
        console.log('Upload successful:', result);

        return {
          filename: file.name,
          chunks_added: 1,
          total_chunks: 1,
          document_id: result.id,
          gcs_url: `research://${result.id}`
        };
      } catch (error) {
        console.error('FastAPI upload error:', error);
        throw error;
      }
    },

    // Get all documents from research items (working backend approach)
    getDocuments: async () => {
      try {
        console.log('Fetching documents from research items:', `${BACKEND_URL}/linkedin/research`);

        const response = await fetch(`${BACKEND_URL}/linkedin/research`);

        console.log('Get documents response status:', response.status);

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Get documents response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Get documents failed: ${response.status}`);
        }

        const researchItems = await response.json();
        console.log('Documents fetched successfully:', researchItems);

        // Filter for document-type research items
        const documents = researchItems.filter(item => 
          item.tags && item.tags.includes('document')
        );

        return documents.map(doc => ({
          filename: doc.source || doc.topic,
          total_chunks: 1,
          upload_date: doc.created_at,
          file_size: doc.data ? doc.data.length : 0,
          file_type: 'research',
          document_id: doc.id,
          gcs_url: `research://${doc.id}`
        }));
      } catch (error) {
        console.error('FastAPI get documents error:', error);
        throw error;
      }
    },

    // Delete document from research items (working backend approach)
    deleteDocument: async (filename) => {
      try {
        console.log('Attempting to delete file:', filename);

        // First get the research item to find its ID
        const response = await fetch(`${BACKEND_URL}/linkedin/research`);
        const researchItems = await response.json();

        const documentItem = researchItems.find(item => 
          (item.source === filename || item.topic === `Document: ${filename}`) && 
          item.tags && item.tags.includes('document')
        );

        if (!documentItem) {
          throw new Error(`Document ${filename} not found`);
        }

        // Delete the research item
        const deleteResponse = await fetch(`${BACKEND_URL}/linkedin/research/${documentItem.id}`, {
          method: 'DELETE'
        });

        if (!deleteResponse.ok) {
          const errorData = await deleteResponse.json().catch(() => ({}));
          console.error('Delete response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Delete failed: ${deleteResponse.status}`);
        }

        const result = await deleteResponse.json();
        console.log('Delete successful:', result);
        return result;
      } catch (error) {
        console.error('FastAPI delete error:', error);
        throw error;
      }
    },

    // Ask questions using research items (working backend approach)
    askQuestion: async (question) => {
      try {
        console.log('Asking question:', question);

        // Clean the question to prevent JSON parsing issues
        const cleanQuestion = question.trim().replace(/[^\w\s\-.,!?]/g, '');

        // Get research items to provide context
        const researchResponse = await fetch(`${BACKEND_URL}/linkedin/research`);

        if (!researchResponse.ok) {
          throw new Error(`Failed to fetch research: ${researchResponse.status}`);
        }

        const researchItems = await researchResponse.json();

        // Find relevant research items
        const relevantItems = researchItems.filter(item => 
          item.topic.toLowerCase().includes(cleanQuestion.toLowerCase()) ||
          item.findings.toLowerCase().includes(cleanQuestion.toLowerCase()) ||
          item.data?.toLowerCase().includes(cleanQuestion.toLowerCase())
        );

        if (relevantItems.length === 0) {
          return {
            answer: "I don't have enough information to answer that question. Please upload some relevant documents or research first.",
            sources: [],
            confidence: 0.0,
            sales_followups: [],
            client_followups: []
          };
        }

        // Use the first relevant item to generate an answer
        const context = relevantItems[0];
        const answer = `Based on the research "${context.topic}": ${context.findings}. ${context.data || ''}`;

        return {
          answer: answer,
          sources: relevantItems.map(item => ({
            title: item.topic,
            content: item.findings,
            source: item.source,
            filename: item.source
          })),
          confidence: 0.8,
          sales_followups: ["Follow up with prospects about this topic", "Share insights with your network"],
          client_followups: ["Schedule a call to discuss this further", "Send additional resources"]
        };
      } catch (error) {
        console.error('FastAPI ask error:', error);
        throw error;
      }
    },

    // Generate LinkedIn content using research papers and creator style
    generateContent: async (prompt, creatorStyle, researchContext) => {
      try {
        console.log('Generating content:', { prompt, creatorStyle, researchContext });

        // Map creator style to creator key
        let creatorKey = 'gary-v'; // default to Gary Vaynerchuk
        if (creatorStyle.toLowerCase().includes('gary')) {
          creatorKey = 'gary-v';
        } else if (creatorStyle.toLowerCase().includes('simon')) {
          creatorKey = 'simon-sinek';
        } else if (creatorStyle.toLowerCase().includes('seth')) {
          creatorKey = 'seth-godin';
        }

        // Get research items to use their IDs
        const researchResponse = await fetch(`${BACKEND_URL}/linkedin/research`);
        const researchItems = await researchResponse.json();
        const researchIds = researchItems.map(item => item.id);

        const response = await fetch(`${BACKEND_URL}/linkedin/generate`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            prompt,
            creator_key: creatorKey,
            research_ids: researchIds
          })
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Content generation response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Content generation failed: ${response.status}`);
        }

        // Handle JSON parsing with error handling
        let result;
        try {
          const responseText = await response.text();
          result = JSON.parse(responseText);
          console.log('Content generation successful:', result);
        } catch (parseError) {
          console.error('JSON parsing error:', parseError);
          console.error('Response text:', await response.text());
          throw new Error('Invalid JSON response from backend');
        }

        // Map the API response to the expected format
        return {
          linkedin_posts: result.linkedin_posts || [],
          video_scripts: result.video_scripts || [],
          hashtags: result.hashtags || [],
          engagement_tips: result.engagement_tips || []
        };
      } catch (error) {
        console.error('FastAPI content generation error:', error);
        throw error;
      }
    },

    // Add research item to Supabase
    addResearch: async (researchItem) => {
      try {
        console.log('Adding research item to Supabase:', researchItem);

        const { data, error } = await supabase
          .from(TABLES.RESEARCH)
          .insert({
            topic: researchItem.topic,
            findings: researchItem.findings,
            data: researchItem.data,
            source: researchItem.source,
            tags: researchItem.tags || 'research, insights'
          })
          .select()
          .single();

        if (error) {
          console.error('Supabase add research error:', error);
          throw new Error(error.message);
        }

        console.log('Research added successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase add research error:', error);
        throw error;
      }
    },

    // Get research items from Supabase
    getResearch: async () => {
      try {
        console.log('Getting research items from Supabase...');

        const { data, error } = await supabase
          .from(TABLES.RESEARCH)
          .select('*')
          .order('created_at', { ascending: false });

        if (error) {
          console.error('Supabase get research error:', error);
          throw new Error(error.message);
        }

        console.log('Research items fetched successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase get research error:', error);
        throw error;
      }
    },

    // Update research item in Supabase
    updateResearch: async (researchId, researchItem) => {
      try {
        console.log('Updating research item in Supabase:', researchId, researchItem);

        const { data, error } = await supabase
          .from(TABLES.RESEARCH)
          .update({
            topic: researchItem.topic,
            findings: researchItem.findings,
            data: researchItem.data,
            source: researchItem.source,
            tags: researchItem.tags
          })
          .eq('id', researchId)
          .select()
          .single();

        if (error) {
          console.error('Supabase update research error:', error);
          throw new Error(error.message);
        }

        console.log('Research updated successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase update research error:', error);
        throw error;
      }
    },

    // Delete research item from Supabase
    deleteResearch: async (researchId) => {
      try {
        console.log('Deleting research item from Supabase:', researchId);

        const { error } = await supabase
          .from(TABLES.RESEARCH)
          .delete()
          .eq('id', researchId);

        if (error) {
          console.error('Supabase delete research error:', error);
          throw new Error(error.message);
        }

        console.log('Research deleted successfully');
        return { success: true };
      } catch (error) {
        console.error('Supabase delete research error:', error);
        throw error;
      }
    },

    // Get creator styles from Supabase
    getCreatorStyles: async () => {
      try {
        console.log('Getting creator styles from Supabase...');

        const { data, error } = await supabase
          .from(TABLES.CREATOR_STYLES)
          .select('*')
          .order('name');

        if (error) {
          console.error('Supabase get creator styles error:', error);
          throw new Error(error.message);
        }

        console.log('Creator styles fetched successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase get creator styles error:', error);
        throw error;
      }
    },

    // Add creator style to Supabase
    addCreatorStyle: async (creatorStyle) => {
      try {
        console.log('Adding creator style to Supabase:', creatorStyle);

        const { data, error } = await supabase
          .from(TABLES.CREATOR_STYLES)
          .insert({
            name: creatorStyle.name,
            key: creatorStyle.key,
            style: creatorStyle.style
          })
          .select()
          .single();

        if (error) {
          console.error('Supabase add creator style error:', error);
          throw new Error(error.message);
        }

        console.log('Creator style added successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase add creator style error:', error);
        throw error;
      }
    },

    // Update creator style in Supabase
    updateCreatorStyle: async (creatorId, creatorStyle) => {
      try {
        console.log('Updating creator style in Supabase:', creatorId, creatorStyle);

        const { data, error } = await supabase
          .from(TABLES.CREATOR_STYLES)
          .update({
            name: creatorStyle.name,
            key: creatorStyle.key,
            style: creatorStyle.style
          })
          .eq('id', creatorId)
          .select()
          .single();

        if (error) {
          console.error('Supabase update creator style error:', error);
          throw new Error(error.message);
        }

        console.log('Creator style updated successfully:', data);
        return data;
      } catch (error) {
        console.error('Supabase update creator style error:', error);
        throw error;
      }
    },

    // Delete creator style from Supabase
    deleteCreatorStyle: async (creatorId) => {
      try {
        console.log('Deleting creator style from Supabase:', creatorId);

        const { error } = await supabase
          .from(TABLES.CREATOR_STYLES)
          .delete()
          .eq('id', creatorId);

        if (error) {
          console.error('Supabase delete creator style error:', error);
          throw new Error(error.message);
        }

        console.log('Creator style deleted successfully');
        return { success: true };
      } catch (error) {
        console.error('Supabase delete creator style error:', error);
        throw error;
      }
    },

    // Crawl website and store as research item (working backend approach)
    crawlWebsite: async (url) => {
      try {
        console.log('Crawling website:', url);

        // Clean the URL to prevent JSON parsing issues
        const cleanUrl = url.trim().replace(/[^\w\-._~:/?#[\]@!$&'()*+,;=%]/g, '');

        // Create a research item for the crawled content
        const researchItem = {
          topic: `Web Content from ${cleanUrl}`,
          findings: `Content crawled from ${cleanUrl}`,
          data: `Web content from ${cleanUrl} - This would contain the actual scraped content in a real implementation.`,
          source: cleanUrl,
          tags: `web-crawl,content`
        };

        console.log('Creating research item for crawled content:', researchItem);

        const response = await fetch(`${BACKEND_URL}/linkedin/research`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(researchItem)
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Crawl response error:', errorData);
          throw new Error(errorData.detail || errorData.error || `Crawl failed: ${response.status}`);
        }

        const result = await response.json();
        console.log('Crawl successful:', result);

        return {
          title: `Web Content from ${cleanUrl}`,
          filename: `Web Content - ${cleanUrl}`,
          chunks_added: 1
        };
      } catch (error) {
        console.error('Crawl error:', error);
        throw error;
      }
    },

    // Get all chunks from backend
    getAllChunks: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/chunks`);

        if (!response.ok) {
          throw new Error(`Get chunks failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Get chunks error:', error);
        return [];
      }
    },

    // Refresh index
    refreshIndex: async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/refresh`, {
          method: 'POST'
        });

        if (!response.ok) {
          throw new Error(`Refresh failed: ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('Refresh error:', error);
        throw error;
      }
    },

    // Health check
    healthCheck: async () => {
      try {
        console.log('Checking backend health...');
