    setMessages(prev => [...prev, userMessage]);
    setIsTyping(true);

    try {
      let creatorStyle = creatorDatabase.find(c => c.key === 'gary-v');

      const lowerText = text.toLowerCase();
      for (const creator of creatorDatabase) {
        if (lowerText.includes(creator.name.toLowerCase()) || 
